import React, { Component } from 'react';
import {Button, Row, Col, Card, CardHeader, CardBody, Label, InputGroup, InputGroupAddon, InputGroupText, Input} from 'reactstrap';
import { connect } from 'react-redux';
import DbCrud from '../../../../Actions/DbCrud';

class Edit extends Component{

    constructor(props){
        super(props);
        this.state = {
            row: []
        };
        this.info = this.info.bind(this);
    }

    componentDidMount(){
        try{var configs = JSON.parse(localStorage.getItem('configs'));
        console.log(configs);}catch(e){}
        this.props.updateConfigs("loadConfigs", "stores/configs/find");
    }

    saveProduct(){
        console.log("guardar");
        console.log(this.state.row);
    }

    changeInputs(idInput){
        var rowTmp = this.state.row;
        var value = document.getElementById(idInput).value;
        if(idInput == "maxMargin") rowTmp.maxMargin = value;
        if(idInput == "protection") rowTmp.protection = value;
        if(idInput == "incentive") rowTmp.incentive = value;
        localStorage.setItem('configs', JSON.stringify(rowTmp));
      }

    info(obj){
        if(obj.configs){
            //localStorage.setItem('configs', JSON.stringify(obj.configs));
            return(
                <Row>
                    <Col>
                        <Label>Margen Max Protegido</Label>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText><i className="fa fa-shield"></i></InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" id="maxMargin" defaultValue={obj.configs.maxMargin} onChange={this.changeInputs.bind(this,"maxMargin")} placeholder="Ingrese el margen a proteger" />
                        </InputGroup>
                    </Col>
                    <Col>
                        <Label>Protecci√≥n Margen Residuo</Label>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText><i className="fa fa-shield"></i></InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" id="protection" defaultValue={obj.configs.protection} onChange={this.changeInputs.bind(this,"protection")} placeholder="Ingrese el margen a proteger" />
                        </InputGroup>
                    </Col>
                    <Col>
                        <Label>Margen Incentivo</Label>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText><i className="fa fa-heart"></i></InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" id="incentive" defaultValue={obj.configs.incentive} onChange={this.changeInputs.bind(this,"incentive")} placeholder="Ingrese el margen a proteger" />
                        </InputGroup>
                    </Col>
                </Row>
            )
        }  
    }

    render(){
        return(
            <Row>
                <Col xs="12" md="12">
                    <Row>
                        <Col xs="12" md="12">
                            <Card>
                                <CardHeader>
                                    <Row>
                                        <Col xs="10" sm="10" md="10">Editar o Crear una nueva tienda</Col>
                                        <Col xs="2" sm="2" md="2">
                                            <Button size="sm" style={{fontSize:"medium"}} onClick={this.saveProduct.bind(this)} block className="float-right btn btn-success active">
                                                Guardar
                                            </Button>
                                        </Col>
                                    </Row>
                                </CardHeader>
                                <CardBody>
                                    {this.info(this.props.obj)}
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Col>
            </Row>
        )
    }

}


const mapStateToProps = (state) => {
    return{
      obj:state.DaoStores
    }
  }

  const mapDispatchToProps = (dispatch) => {
    return {
      updateConfigs: (type, colletion) => {
        DbCrud.dbMLoad(dispatch, type, colletion, {}, "POST");
      }
    }
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Edit);
