import { connect } from 'react-redux';
import React, { Component } from 'react';
import DbCrud from '../../../Pojo/DbCrud';
import {
  Badge, Button, ButtonDropdown, Card, CardBody, CardFooter,
  CardHeader, Col, Collapse, DropdownItem, DropdownMenu,
  DropdownToggle, Fade, Form, FormGroup, FormText, FormFeedback,
  Input, InputGroup, InputGroupAddon, InputGroupText, Label, Row,
} from 'reactstrap';

import { TextMask, InputAdapter } from 'react-text-mask-hoc';

var flat = true;

class MyAccount extends Component {

  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.toggleFade = this.toggleFade.bind(this);
    this.state = {
      collapse: true,
      fadeIn: true,
      timeout: 300
    };
  }

  componentDidMount(){this.props.dispReducer('loadCompany',"company", "email",this.props.email);}

  componentDidUpdate(){
    if(flat){
      flat=false;
      //this.props.dispReducer('loadCompany', "company", "email",this.props.email);
    }
  }


  toggle() {
    this.setState({ collapse: !this.state.collapse });
  }

  toggleFade() {
    this.setState((prevState) => { return { fadeIn: !prevState }});
  }

  callCountryList(){this.props.history.push('/Geo/CountriesList');}

  callSave(e){
    e.preventDefault();
    this.props.dispReducer('saveCountry', "country", this.refs );
  }

  render() {
    return (
      <div className="animated fadeIn">
        <Row>
          <Col xs="12" sm="12">
            <Card>
              <CardHeader>
                <strong>Crear nuevo Pa√≠s</strong>
                <Button color="secondary" onClick={this.callCountryList.bind(this)} className="float-right"><i className="fa icon-arrow-left-circle"></i>&nbsp;Regresar</Button>
              </CardHeader>
              <CardBody>

              <Form className="form-horizontal" onSubmit={this.callSave.bind(this)}>
                  <FormGroup row className="my-0">
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="countryName">Nombre Pais</Label>
                        <InputGroup >
                          <TextMask ref="countryName" id="countryName" 
                            mask={[/[A-Z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,
                              /[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,
                              /[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,
                              /[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/]}
                            Component={InputAdapter}
                            className="form-control" placeholder="Nombre Pais"
                          />
                        </InputGroup>
                      </FormGroup>
                    </Col>
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="isoCode">Codigo ISO</Label>
                        <InputGroup >
                          <TextMask ref="isoCode" id="isoCode"
                            mask={[/[A-Z]/,/[A-Z]/,/[A-Z]/]}
                            Component={InputAdapter}
                            className="form-control" placeholder="Codigo ISO"
                          />
                        </InputGroup>
                      </FormGroup>
                    </Col>
                  </FormGroup>

                  <FormGroup row className="my-0">
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="type">Estado</Label>
                        <Input ref={active => { this.select = active; }} defaultValue="1" type="select" name="active" id="active" required>
                          <option value="100999" selected="selected">Activo</option>
                          <option value="200999" >Inactivo</option>
                        </Input> 
                      </FormGroup>
                    </Col>
                    <Col xs="3">
                      <FormGroup>
                        <Label htmlFor="Longitud">Geo Latitud</Label>
                        <TextMask ref="Longitud" id="Longitud"
                            mask={[/[\-\+]/,/[1-9]/,/\d/,'.',/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,]}
                            Component={InputAdapter}
                            className="form-control" placeholder="Longitud"
                          />
                      </FormGroup>
                    </Col>
                    <Col xs="3">
                      <FormGroup>
                        <Label htmlFor="Latitud">Geo Longitud</Label>
                        <TextMask ref="Latitud" id="Latitud"
                            mask={[/[\-\+]/,/[1-9]/,/\d/,'.',/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,]}
                            Component={InputAdapter}
                            className="form-control" placeholder="Latitud"
                          />
                      </FormGroup>
                    </Col>
                  </FormGroup>
                      <div className="form-actions">
                        <Button color="primary" type="submit" value="Submit">Guardar</Button>&nbsp;&nbsp;
                        <Button color="secondary" onClick={this.callCountryList.bind(this)}>Cancel</Button>
                      </div>
                </Form>

              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
    return{
      email: state.login.email,
      password: state.login.password,
      obj: state.MyAccount
    }
}

const mapDispatchToProps = (dispatch) => {
    return{ 
      dispReducer: (type, colletion, value) => { 
        DbCrud.dbCreate(dispatch, type, colletion, value);
      } 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
