import firebase from '../Pojo/Firebase.js';
let db = firebase.firestore();

// Estructura de DB para Usuario
const geo = [
    {
        active : undefined,
        countryName :   undefined,
        geopoint : [undefined,undefined],
        isoCode:   undefined,
        states:{
            0   :  {
                cities   :   {
                    code:  undefined,
                    geopoint    :  [undefined,undefined],
                    nameCity: undefined
                },
                code :   undefined,
                geopoint    :  [undefined,undefined],
                isoCode   :   undefined,
                stateName  :   undefined
            }
        }
    }
]

//se pasa un estado por defecto para definicion de estructura
function Geo(state = [], action){
    var NewState = Object.assign([],state);
    if(action.type == "loadGeo"){
        action.data.forEach(function (doc) {

            var object = JSON.stringify(doc.data());
            object = object.substring(1,object.length-1);
            var AddElement = JSON.stringify({"id":doc.id});
            object = eval('{' + object +','+  AddElement.substring(1,AddElement.length-1) + '}');

            NewState.push(object);
            console.log(NewState);
        });
        return NewState;
    }else return NewState;

}

const DaoGeo = {Geo}

export default DaoGeo;
