import { connect } from 'react-redux';
import React, { Component } from 'react';
import { CSVLink } from "react-csv";
import { Row, Form, FormGroup, NavLink,Input, CardHeader, Col, CardBody, Card } from 'reactstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import CurrencyFormat from 'react-currency-format';
import DbCrud from '../../../Pojo/DbCrud';


class Edit extends Component {

  constructor(props) {
    super(props);
    this.state = {
      orders: {
        pag:"1",
        limit:"20",
        filFile:"order",
        filValue: ""+this.props.match.params.Order,
        sorts:[
          {
            file:"oderDate",
            value:1
          }
        ]
      }
    }

  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    this.props.loadReducer('loadOrdersEdit', "orders/find/edit",this.state.orders);
  }

  callList() { 
    this.props.history.push('/Orders/List'); 
  }

  callSave(e) {
    e.preventDefault();
    //this.props.update('updateGeo', "geo", "CountriesList", this, this.state.documentId);
    this.props.history.push('/Orders/List');
  }

  selects(select, valSeleted){
    const formOrders = [];
    var stausNodes = "";
    if(select == "suppliers") stausNodes = this.props.obj.listOrdersEditSuppliers; 
    if(select == "status") stausNodes = this.props.obj.listOrdersEditStatus; 
    Object.keys(stausNodes).forEach(function(key){
      var keys = "status"+key;
      if(valSeleted == stausNodes[key]["name"])
      formOrders.unshift(<option key={keys} value={key}>{stausNodes[key]["name"]}</option>);
      else
      formOrders.push(<option key={keys} value={key}>{stausNodes[key]["name"]}</option>);
    })
    return formOrders;
  }

  formOrders(){
    const nodes = this.props.obj.listOrdersEdit;
    const formOrders = [];
    const reference = this.props.match.params.Reference;
    const selects = this.selects.bind(this);
    var flag = true;
    Object.keys(nodes).forEach(function(key){
      var keyname = "order"+key;
      var color = (reference == nodes[key].reference)? "#a9a9a347":"";
      if(flag){
        flag = false;
        formOrders.push(
          <CardBody key="headOrder">
            <Row>
              <Col xs="3" md="3">
                <Row>
                  <Col xs="12" md="12"><strong>Orden:</strong> {nodes[key].order}</Col>
                  <Col xs="12" md="12"><strong>Cliente:</strong> {nodes[key].customer}</Col>
                  <Col xs="12" md="12"><strong>DNI:</strong> {nodes[key].dni}</Col>
                </Row>
              </Col>
              <Col xs="3" md="3">
