import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Row, Col, Input, Card, CardHeader, CardBody } from 'reactstrap';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Actions/DbCrud';

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class Eidts extends Component {

  constructor(props) {
    super(props);
    this.state = {
        ini : ""
    }
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    this.props.loadReducer("loadCombinations", "combinations/", {});
  } 

  option(){

      var options = [];
      var label = this.props.obj.totalcombinations.label;

      label.forEach((elemt, item) => {
        options.push(<option key={item} value={elemt}>{elemt}</option>)
      });

      return options;
  }

  render() {

    return (
      <Card>
        <CardHeader>
          Administrar Combinaci√≥n
        </CardHeader>
        <CardBody>
          <Row>
            <Col xs="6" md="6">
                <Row>
                    <Col>
                        <Input type="select" name="combinacion" id="combinacion" key="combinacion">
                            {this.option()}
                        </Input>
                    </Col>
                    <Col>Nombre</Col>
                </Row>
            </Col>
          </Row>
        </CardBody>
      </Card>
    );
  }
}

const mapStateToProps = (state) => {
  return{
    obj:state.DaoCombinations
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    }

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Eidts);
