import { connect } from 'react-redux';
import React, { Component } from 'react';
import { Row, Form, FormGroup, CardHeader, Col, CardBody, Card } from 'reactstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Pojo/DbCrud';

var flat = true;

class Edit extends Component {

  constructor(props) {
    super(props);
    this.state = {
      orders: {
        pag:"1",
        limit:"20",
        filFile:"order",
        filValue: ""+this.props.match.params.Order,
        sorts:[
          {
            file:"oderDate",
            value:1
          }
        ]
      }
    }

  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    if (flat) {
      flat = false;
      this.props.loadReducer('loadOrders', "orders/find",this.state.orders);
    }
  }

  callList() { 
    this.props.history.push('/Orders/List'); 
  }

  callSave(e) {
    e.preventDefault();
    //this.props.update('updateGeo', "geo", "CountriesList", this, this.state.documentId);
    this.props.history.push('/Orders/List');
  }

  onFormSubmit = (e) => {
    e.preventDefault();
    const target = e.target;
    var tmpfFile = "";
    var tmpfValue = "";

    //Se selecciona de atras para delante un solo filtro 
    if(target.customer.value != ""){
      tmpfFile = "customer";
      tmpfValue = target.customer.value+"";
    }else{
      if(target.dni.value != ""){
        tmpfFile = "dni";
        tmpfValue = target.dni.value+"";
      }else{
        if(target.order.value != ""){
          tmpfFile = "order";
          tmpfValue = target.order.value+"";
        }else{
          if(target.supplier.value != "0"){
            tmpfFile = "supplier";
            tmpfValue = target.supplier.value+"";
          }else{
            if(target.brand.value != "0"){
              tmpfFile = "brand";
              tmpfValue = e.target.brand.value+"";
            }else{
              if(target.store.value != "0"){
                tmpfFile = "store";
                tmpfValue = e.target.store.value+"";
              }else{
                if(target.status.value != "0"){
                  tmpfFile = "status";
                  tmpfValue = e.target.status.value+"";
                  console.log(tmpfValue);
                }
              }
            }
          }
        }
      }
    }

    if(tmpfValue == "0") tmpfValue = "";

    //Se realiza la solicitud nuevamente para recargar los registros 
    this.props.loadReducer('loadOrders', "orders/find", {
      pag:1,
      limit:5,
      filFile:tmpfFile,
      filValue:tmpfValue,
      sorts:this.state.orders.sorts
    });

    //Actualizamos el esta con los valores de filtros seleccionados esto por si se utiliza la 
    //paginacion por lo que se requiere que el estado mantenga el filtro de campo 
    this.setState({
        orders:{
          pag:1,
          limit:5,
          filFile:tmpfFile,
          filValue:tmpfValue,
          sorts:this.state.orders.sorts
        }
      }
    );
  }

  formOrders(){
    const nodes = this.props.obj.listOrders;
    const formOrders = [];
    const onSubmit = this.onFormSubmit.bind();
    var flag = true;
    Object.keys(nodes).forEach(function(key){
      var keyname = "order"+key;
      if(flag){
        flag = false;
        formOrders.push(
          <CardBody key="headOrder">
            <Row>
              <Col xs="4" md="4">Orden: {nodes[key].order}</Col>
              <Col xs="4" md="4">Fecha: {(nodes[key].date).substr(0,10)}</Col>
              <Col xs="4" md="4">Cliente: {nodes[key].customer}</Col>
              <Col xs="4" md="4">DNI: {nodes[key].dni}</Col>
              <Col xs="4" md="4">Celular: {nodes[key].phone}</Col>
              <Col xs="4" md="4">Departamento: {nodes[key].state}</Col>
              <Col xs="4" md="4">Ciudad: {nodes[key].city}</Col>
              <Col xs="4" md="4">Direcci√≥n: {nodes[key].address} {nodes[key].addAddress} - {nodes[key].neighborhood}</Col>
            </Row>
          </CardBody>
        )
      }
      formOrders.push(
          <Row key={keyname} xs="12" md="12">
            <CardBody>
              <Form action="" onSubmit={onSubmit} method="post" className="form-horizontal">
                  <FormGroup row>
                  <Col xs="12" md="12">
                      <Row>
                        <Col xs="2" md="2">Orden: {nodes[key].order}</Col>
                        <Col xs="2" md="2">Fecha: {(nodes[key].date).substr(0,10)}</Col>
                        <Col xs="4" md="4">Cliente: {nodes[key].customer}</Col>
                      </Row>
                  </Col>
                  </FormGroup>
              </Form>
            </CardBody>
          </Row>
      )
    })
    return formOrders;

  }

  render() {

    return (
      <div className="animated fadeIn">
        <Card>
          {this.formOrders()}
        </Card>
      </div>
    );
  }

}

const mapStateToProps = (state) => {
  //Registra el objeto la funcion que cargara el objeto en el estado
  return{
    obj:state.DaoOrders
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Edit);
