import firebase from '../Pojo/Firebase.js';

const user = {email:"email", password:"password"}

//se pasa un estado por defecto para definicion de estructura
const reducerLogin = (state = user, action) => {
    // console.log("Llamada Dispacth reducerLogin ");
    var NewState = Object.assign({},state);
    /*if(action.type == "loadUser"){
        NewState.email = action.user.email;
        NewState.password = action.user.uid;
        return NewState;
    }
    if(action.type == "lInput"){
        if(action.idInput == "email") NewState.email = action.value;
        if(action.idInput == "password") NewState.password = action.value;
        return NewState;
    }
    if(action.type == "login"){
        console.log(NewState.email)
        firebase.auth().signInWithEmailAndPassword(NewState.email, NewState.password)
        .then(
            function(){window.location = "/"}
        )
        .catch(function(error) {
            var errorCode = error.code;
            var errorMessage = error.message;
            console.log("Usuario y/o Contrase침a incorrecta "+errorCode+" - "+errorMessage);
        });
    }
    if(action.type == "logout"){
        firebase.auth().signOut().then(function() {
            window.location = "#/login";
        }).catch(function(error) {
            console.log("Se ha precentado un erro al salir de la aplicaci칩n "+error)
        });
    }*/
    return state;
} 

const reducerTareas = (state = [], action) => {
    // console.log("Llamada Dispacth reducerTareas");
    var NewState = Object.assign({},state);
    if(action.type == "jobs"){return NewState;}
    return state;
}

const DaoLogin = {reducerLogin, reducerTareas}

export default DaoLogin;
