                                        </Col>
                                        <Col>
                                            <Label>Fee</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-money"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="fee" placeholder="Ingrese el fee" defaultValue={this.state.row.fee} onChange={this.changeInputs.bind(this,"fee")}/>
