                                        </Col>
                                        <Col>
                                            <Label>Fee Pago</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-credit-card"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="feePay" placeholder="Ingrese el fee pago" defaultValue={this.state.row.feePay} onChange={this.changeInputs.bind(this,"feePay")}/>
