                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-cart-arrow-down"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="discount" placeholder="Ingrese el descuento" defaultValue={this.state.row.discount} onChange={this.changeInputs.bind(this,"discount")}/>
                                            </InputGroup>
                                        </Col>
                                        <Col>
                                            <Label>Bins</Label>
                                            <InputGroup>
                                                <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-barcode"></i></InputGroupText>
                                                </InputGroupAddon>
                                                <Input type="text" id="bins" placeholder="Ingrese los bins" defaultValue={this.state.row.bins} onChange={this.changeInputs.bind(this,"bins")}/>
                                            </InputGroup>
                                        </Col>
                                        <Col>
                                            <Label>PSE</Label>
                                            <Select 
                                                name="stores" 
                                                value={this.state.selPse || ['ion']}
                                                options={pse} 
                                                onChange={this.addPse} 
                                                multi 
                                            />
                                        </Col>
                                        <Col>
                                            <Label>Email</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-envelope-o"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="email" placeholder="Ingrese el correo" defaultValue={this.state.row.email} onChange={this.changeInputs.bind(this,"email")}/>
                                            </InputGroup>
                                        </Col>
                                    </Row>
                                    <Row className="pb-3">
                                        <Col xs="2" sm="2" md="2">
                                            <Label>Catalogue</Label><br/>
                                            {this.catalogue()}
                                        </Col>
                                        <Col>
                                            Tipo Banco, Fondo, Empresa, Proveedor
