                                    <Row className="pb-5">
                                        <Col xs="10" sm="10" md="10">           
                                            <Label>Descripción</Label>
                                            <InputGroup>
                                                <ReactQuill value={this.state.row.description || ''} modules={this.editor} onChange={this.description()} />
                                            </InputGroup>
                                        </Col>
                                    </Row>

                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Col>

            </Row>

        )
    }
}

