import React, { Component } from 'react';
import { Row, Col, Pagination, PaginationItem, PaginationLink, Input } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDoubleLeft, faAngleLeft, faAngleRight, faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons';


class ObjPag extends Component {

    constructor(props) {
        super(props);
        pagLoad = this.props.obj.pagination;
    }

    callPagination(pagNum = "",limit = ""){
        if(limit == "") limit = this.pagLoad.limit;

        if(pagNum == "back"){
            pagNum = parseInt(this.state.orders.pag)-1;
        }

        if(pagNum == "forward"){
            pagNum = parseInt(this.state.orders.pag)+1;
        }

        //Si el valor de registros visualizados por pagina cambia y se recupera la pagina en la que nos encontramos
        if(pagNum == "cRegister"){
            pagNum = this.state.orders.pag;
        }

        console.log("pagNum: "+pagNum+" limit: "+limit)

    }


    paginationRender(rendPage, pag){
        //console.log(rendPage+" --  "+pag);
        var start = (pag-10)+1;
        if(start <= 0) start = 1;
        var end =  start+10;
        //this.props.pagLoad.bind(this,""+i)
        //console.log("ini val "+start+" End: "+end+" Pag: "+pag);
        const tempPag = [];
        for (let i=start; i<end; i++) {
            if(i == pag){
                tempPag.push(
                    <PaginationItem key={"pag"+i} active>
                        <PaginationLink onClick={this.callPagination.bind(""+i)}>
                            {i}
                        </PaginationLink>
                    </PaginationItem>
                )
            }else{
                tempPag.push(
                    <PaginationItem key={"pag"+i}>
                        <PaginationLink onClick={this.props.pagLoad.bind(this,""+i)}>
                            {i}
                        </PaginationLink>
                    </PaginationItem>
                )
            }
        }
        return tempPag;
    }

    pagRender(e,value){
        //const RegPerFind = this.props.info.RegPerFind;
        const NumPages = this.props.info.NumPages;
        //const limit = this.props.info.limit;
        const pag = this.props.info.pag;
        var rendPage = 0;
        rendPage = (NumPages < 10)? NumPages : 10;

        const pagTemp = this.paginationRender(rendPage,pag);
        if(pag<=10){
            return (
                <Pagination >
                    <PaginationItem disabled key="allback">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, 'allback')}>
                            <FontAwesomeIcon  icon={faAngleDoubleLeft} />
                        </PaginationLink>
                    </PaginationItem>
                    <PaginationItem disabled key="back">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, "back")}>
                            <FontAwesomeIcon  icon={faAngleLeft} />
                        </PaginationLink>
                    </PaginationItem>
                    {pagTemp}
                    <PaginationItem key="forward">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, "forward")}>
                            <FontAwesomeIcon  icon={faAngleRight} />
                        </PaginationLink>
                    </PaginationItem>
                    <PaginationItem key="allforward">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, "allforward")}>
                            <FontAwesomeIcon  icon={faAngleDoubleRight} />
                        </PaginationLink>
                    </PaginationItem>
                </Pagination>
            );
        }
        else{
            return (
                <Pagination >
                    <PaginationItem  key="allback">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, 'allback')}>
                            <FontAwesomeIcon  icon={faAngleDoubleLeft} />
                        </PaginationLink>
                    </PaginationItem>
                    <PaginationItem  key="back">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, "back")}>
                            <FontAwesomeIcon  icon={faAngleLeft} />
                        </PaginationLink>
                    </PaginationItem>
                    {pagTemp}
                    <PaginationItem key="forward">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, "forward")}>
                            <FontAwesomeIcon  icon={faAngleRight} />
                        </PaginationLink>
                    </PaginationItem>
                    <PaginationItem key="allforward">
                        <PaginationLink onClick={this.props.pagLoad.bind(this, "allforward")}>
                            <FontAwesomeIcon  icon={faAngleDoubleRight} />
                        </PaginationLink>
                    </PaginationItem>
                </Pagination>
            );
        }

    }

    option(e,value){
        const temp = [];
        temp.push(<option key={"opt0"} value="3333">{this.props.info.limit}</option>);
        if(this.props.info.limit != 5)temp.push(<option key="opt5" value="5">5</option>);
        if(this.props.info.limit != 10)temp.push(<option key="opt10" value="10" >10</option>);
        if(this.props.info.limit != 20)temp.push(<option key="opt20" value="20" >20</option>);
        if(this.props.info.limit != 50)temp.push(<option key="opt50" value="50">50</option>);
        if(this.props.info.limit != 100)temp.push(<option key="opt100" value="100" >100</option> );
        return temp;
    }
    render(){

        const propss = this.props.pagLoad.bind();

        return (
            <Row>
                <Col>
                    <Input type="select" name="select" id="pagNum" onChange={ 
                        (event) => {
                            const limit = event.target.value;
                            propss("cRegister",limit);
                        }
                    }> 
                        {this.option()}      
                    </Input>
                </Col>
                <Col>
                    {this.pagRender()}
                </Col>
            </Row>

        );
    }
}

export default ObjPag;
