import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Row,InputGroup, InputGroupAddon, InputGroupText, Form, FormGroup, Label, Input, Col, Card, CardHeader, CardBody, Modal, ModalHeader, ModalBody, ModalFooter, Button, ButtonGroup } from 'reactstrap';
import { AppSwitch } from '@coreui/react';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Pojo/DbCrud';


import Data from './Enumerators/Data';
const objStatus = Data.StatusOrder;
const objStores = Data.Stores;

var flat = true;

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class OrdersList extends Component {

  constructor(props) {
    super(props);
    this.state = {
      orders: {
        pag:"1",
        limit:"3",
        filFile:"customer",
        filValue: "Luisa",
        sorts:[
          {
            file:"customer",
            value:1
          },
          {
            file:"skuName",
            value:-1
          }
        ]
      },

      groups : {
        pag:"1",
        limit:"3",
        filFile:"group",
        filValue:"StatusOrder",
        sorts:[
            {
              file:"code",
              value:1
            },
        ]
      }
    }
    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 3,
      hidePageListOnlyOnePage: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false
    }


    //this.toggleDanger = this.toggleDanger.bind(this);
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    if (flat) {
      flat = false;
      this.props.loadReducer('loadOrders', "orders/find",this.state.orders);
      //this.props.loadReducer('loadOrders', "orders/find",this.state.groups);
    }
  }

  //Funcion que permite hacer la llamada al formulario para editar un nuevo registro 
  callEdit(row) {
    this.props.history.push({
      pathname: '/Geo/CountriesAdd',
      row: row
    })
  }

  //Cambia el valor a visible del mensaje de alerta 
  //guarda el identificador de la fila que sera afectada 
  toggleDanger(row) {
    this.setState({
      danger: !this.state.danger,
      tmpRow: row.documentId
    });
  }


  //Funcion que construye los botones de ediccion para cada registro listado en la tabla 
  editFormatter(cell, row) {
    return (
      <ButtonGroup>
        <Button onClick={this.callEdit.bind(this, row)} size="sm" className="btn-spotify btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
          <i className="icon-pencil"></i>
        </Button>
        <Button onClick={this.toggleDanger.bind(this, row)} size="sm" className="btn-reddit btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
          <i className="icon-trash"></i>
        </Button>
      </ButtonGroup>
    )
  }

  render() {
    return (
      <div className="animated">
        <Card>
          <CardHeader>
            Lista Ordenes
            <div className="card-header-actions">
              <a href="https://github.com/AllenFang/react-bootstrap-table" rel="noopener noreferrer" target="_blank" className="card-header-action">
                <small className="text-muted">Aprende</small>
              </a>
            </div>
          </CardHeader>
          <CardBody>

            <Form action="" method="post" className="form-horizontal">
              <FormGroup row>
              <Col xs="12" md="12">
                <Row>
                  <Col xs="4" md="4">
                    <Input type="select" name="select" id="objStatus">
                    {objStatus.map((e, key) => {
                        return <option value={e.value} disabled={e.disabled} selected={e.selected}>{e.label}</option>;
                    })}
                    </Input>
                  </Col>
                  <Col xs="4" md="4">
                    <Input type="select" name="select" id="objStores">
                    {objStores.map((e, key) => {
                        return <option value={e.value} disabled={e.disabled} selected={e.selected}>{e.label}</option>;
                    })}
                    </Input>
                  </Col>
                  <Col xs="4" md="4">
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa icon-layers"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input type="text" id="input1-group1" name="input1-group1" placeholder="# Orden" />
                      </InputGroup>
                    </Col>
                </Row>
              </Col>
              </FormGroup>
            </Form>

          </CardBody>

          <CardBody>
            <Button color="primary"><i className="fa icon-plus"></i>&nbsp;Crear Estado</Button>
            <Button className="float-right" color="secondary" className="float-right">
              <i className="fa icon-arrow-left-circle"></i>&nbsp;Regresar
            </Button>
            <BootstrapTable data={this.props.obj.listOrders} striped hover pagination search options={this.options}>
              <TableHeaderColumn dataField='status' dataSort>Estado</TableHeaderColumn>
              <TableHeaderColumn width={'10%'} dataField='order' >Orden</TableHeaderColumn>
              <TableHeaderColumn dataField='reference' >Referencia</TableHeaderColumn>
              <TableHeaderColumn width={'30%'} dataField='skuName' >Producto</TableHeaderColumn>
              <TableHeaderColumn dataField='city' >Ciudad</TableHeaderColumn>
              <TableHeaderColumn dataField='guide' >Guia</TableHeaderColumn>
              <TableHeaderColumn dataField='invoice' >Factura</TableHeaderColumn>
              <TableHeaderColumn dataField='unit' >Procesado</TableHeaderColumn>
              <TableHeaderColumn dataField='store' >Tienda</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.editFormatter.bind(this)} dataAlign='right' >Accion</TableHeaderColumn>
              <TableHeaderColumn isKey={true} dataField='_id' hidden>Identifier ID</TableHeaderColumn>
            </BootstrapTable>
          </CardBody>
        </Card>
      </div>
    );
  }

}

const mapStateToProps = (state) => {
  //Registra el objeto la funcion que cargara el objeto en el estado
  return{
    obj:state.DaoOrders
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    //type: se utiliza para identificar la accion en el store 
    //colletion: la collection de la base de datos
    //component: componente a resetear para que el cambio se vea automaticamente 
    //documentId: Identificador del registro a modificar 
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    },

    deleteReducer: (type, colletion, component, documentId) => { 
      //DbCrud.dbDelete(dispatch, type, colletion, component, documentId); 
    },

    editFileReducer: (type, colletion, component, documentId, fileEdit, newFileEdit) => { 
      //DbCrud.dbEditFile(dispatch, type, colletion, component, documentId, fileEdit, newFileEdit); 
    }

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(OrdersList);
