import React, { Component } from 'react';
import { connect } from 'react-redux';
import { CardImg, FormGroup, Row, Label, InputGroupAddon, InputGroup, InputGroupText, Col,  Input, Card, CardHeader, CardBody, Nav, NavItem, NavLink, TabContent, TabPane } from 'reactstrap';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Actions/DbCrud';
import { AppSwitch } from '@coreui/react';
import ReactQuill from 'react-quill';
import classnames from 'classnames';
import 'quill/dist/quill.snow.css';
import "./Products.css";

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class Eidts extends Component {

  constructor(props) {
    super(props);
    this.state = {
       activeTab: '1',
       row : JSON.parse(localStorage.getItem("row")),
       text: ""
    }
    this.toggle = this.toggle.bind(this);
    this.categories = this.categories.bind(this)
    this.description = this.description.bind(this);
    this.sdescription = this.sdescription.bind(this);
    this.selects = this.selects.bind(this);
    this.editor = {
      toolbar: [
        ['bold', 'italic', 'underline', 'strike'],        // toggled buttons
        ['blockquote', 'code-block'],
        [{ 'header': 1 }, { 'header': 2 }],               // custom button values
        [{ 'list': 'ordered'}, { 'list': 'bullet' }],
        [{ 'script': 'sub'}, { 'script': 'super' }],      // superscript/subscript
        [{ 'indent': '-1'}, { 'indent': '+1' }],          // outdent/indent
        [{ 'direction': 'rtl' }],                         // text direction
        [{ 'size': ['small', false, 'large', 'huge'] }],  // custom dropdown
        [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
        [{ 'color': [] }, { 'background': [] }],          // dropdown with defaults from theme
        [{ 'image': 1 }],
        [{ 'align': [] }],
        ['clean']                                         // remove formatting button
      ]
    }
  }

  toggle(tab) {
    localStorage.setItem('activeTab',tab);
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab,
      });
    }
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
      var rowTmp = this.state.row;
      if(this.state.row.description == undefined) rowTmp.description = "";
      if(this.state.row.sdescription == undefined) rowTmp.sdescription = "";
      this.setDate(rowTmp);
  }

  status(cell){
    if(this.state.row.status){
        return(
            <AppSwitch id="status" onChange={this.changeInputs.bind(this,"status")} className={'mx-1'} variant={'pill'} color={'success'} label checked/>
        );
      }else{
        return(
           <AppSwitch id="status" onChange={this.changeInputs.bind(this,"status")} className={'mx-1'} variant={'pill'} color={'success'} label  />
        );
      }
  }

  saveLocalStorage(rowTmp){
    localStorage.setItem('row', JSON.stringify(rowTmp));
  }

  setDate(value){
    var tabAct = localStorage.getItem("activeTab");
    this.setState({activeTab:(tabAct)?tabAct:'1', row:value});
    this.saveLocalStorage(value);
  }

  description(value) {
    var rowTmp = this.state.row;
    rowTmp.description = value;
    this.setDate(rowTmp);
  }

  sdescription(value) {
      var rowTmp = this.state.row;
      rowTmp.sdescription = value;
      this.setDate(rowTmp);
  }

  clickCategories(id, name){
    var rowTmp = this.state.row;
    if(document.getElementById("cat_"+id).checked){
      rowTmp.categories.push({id:id, name:name});
    }
    else{
      var TempCate = [];
      rowTmp.categories.forEach((val) => {
        if(val.id != id) TempCate.push(val);
      })
      rowTmp.categories = TempCate;
    }
    this.setDate(rowTmp);
    //rowTmp.categories = value;
    //this.setDate(rowTmp);

  }

  categories(){
   const listCategories = JSON.parse(localStorage.getItem("listCategories"));
   const cateObj = this.state.row.categories;
   var obj = [];
   var flag = false;
   var key = "";
   try{
    listCategories.forEach(element => {
      cateObj.forEach((val) => { if(element._id == val.id)flag = true;})
      key = "catChec"+element._id;
      obj.push(
        <Col key={key} xs="3" sm="3" md="3">
          <FormGroup  check className="checkbox">
            <Input className="form-check-input" type="checkbox" id={"cat_"+element._id} name={element.name} value={element._id} onClick={this.clickCategories.bind(this,element._id,element.name)} defaultChecked={flag}/>
            <Label check className="form-check-label">
              {element.name}
            </Label>
          </FormGroup>
        </Col>
      );

      flag = false;
    });
   }catch(e){}
    return obj;
  }

  changeInputs(idInput){
    var rowTmp = this.state.row;
    var value = document.getElementById(idInput).value;
    if(idInput == "code") rowTmp.code = value;
    if(idInput == "reference") rowTmp.reference = value;
    if(idInput == "name") rowTmp.name = value;
    if(idInput == "supplier") rowTmp.idSupplier = value;
    if(idInput == "brand") rowTmp.idBrand = value;
    if(idInput == "status") rowTmp.status = document.getElementById(idInput).checked;
    if(idInput == "tax") rowTmp.idTax = value;
    if(idInput == "dollar") rowTmp.dollar = value;
    if(idInput == "stock") rowTmp.stock = value;
    this.setDate(rowTmp);
  }

  selects(name, objSelect, id){
    var listObj = JSON.parse(localStorage.getItem(objSelect));
    var opTmp = [];
    var key = "";
    listObj.forEach((val) => {
      key = name+"_"+val._id;
      if(id == val._id) 
        opTmp.unshift(<option key={key} value={val._id}>{val.name}</option>)
      else
        opTmp.push(<option key={key} value={val._id}>{val.name}</option>)
     });

    return (
      <Input type="select" id={name} onChange={this.changeInputs.bind(this,name)} name={name} key={name}>
        {opTmp}
      </Input>
    );


  }

  render() {

    return (
      <Row>
        <Col xs="12" md="12">
            <Row>
                <Col xs="12" md="12">
                    <Card>
                        <CardHeader>
                            Editar o Crear un nuevo producto
                        </CardHeader>
                        <CardBody>
                            <Nav tabs>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '1' })} onClick={() => { this.toggle('1'); }}>
                                  <i className="icon-note"></i> <span> Descripcin</span>
                                </NavLink>
                              </NavItem>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '2' })}  onClick={() => { this.toggle('2'); }} >
                                  <i className="icon-wallet"></i> <span> Precios</span>
                                </NavLink>
                              </NavItem>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '3' })} onClick={() => { this.toggle('3'); }} >
                                  <i className="icon-picture"></i> <span> Imagenes y Videos</span>
                                </NavLink>
                              </NavItem>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '4' })} onClick={() => { this.toggle('4'); }} >
                                  <i className="icon-tag"></i> <span> Descuentos</span>
                                </NavLink>
                              </NavItem>
                            </Nav>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="1">
                                <Row>
                                  <Col>
                                    <Label>Codigo (EAN, SKU, UPC): </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-barcode"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="code" placeholder="Ingrese el cdigo" required defaultValue={this.state.row.code} onChange={this.changeInputs.bind(this,"code")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Referencia</Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="reference" placeholder="Ingrese la referencia" defaultValue={this.state.row.reference} onChange={this.changeInputs.bind(this,"reference")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col className="pb-4">
                                    <Label>Estado: </Label>
                                    <InputGroup>
                                      {this.status()}
                                    </InputGroup>
                                  </Col>
                                </Row>
                                <Row className="pb-3">
                                  <Col>
                                    <Label>Nombre: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-edit"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="name" placeholder="Ingrese el cdigo" required defaultValue={this.state.row.name} onChange={this.changeInputs.bind(this,"name")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Marca: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-cubes"></i></InputGroupText>
                                      </InputGroupAddon>
                                      {this.selects("brand","listBrands", this.state.row.idBrand)}
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Vendedor: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-reddit-alien"></i></InputGroupText>
                                      </InputGroupAddon>
                                      {this.selects("supplier","listSuppliers", this.state.row.idSupplier)}
                                    </InputGroup>
                                  </Col>
                                </Row>
                                <Row>
                                  <Col>
                                    <Label>Categorias: </Label>
                                    <Card>
                                      <Row style={{padding:"10px"}}>
                                        {this.categories()}
                                      </Row>
                                    </Card>
                                  </Col>
                                  {/*<Col>Caracteristicas</Col>
                                  <Col>SEO</Col>*/}
                                </Row>
                                <Row>
                                  <Col><ReactQuill value={this.state.row.description} modules={this.editor}  onChange={this.description}/></Col>
                                  <Col><ReactQuill value={this.state.row.sdescription} modules={this.editor} onChange={this.sdescription} /></Col>
                                </Row>
                              </TabPane>
                            </TabContent>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="2">
                                <Row className="pb-3">
                                  <Col>
                                    <Label>Impuesto: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-barcode"></i></InputGroupText>
                                      </InputGroupAddon>
                                      {this.selects("tax","listTaxes", this.state.row.idTax)}
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Dolar:</Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="dollar" placeholder="Ingrese la referencia" defaultValue={this.state.row.dollar} onChange={this.changeInputs.bind(this,"dollar")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Inventario:</Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="stock" placeholder="Ingrese el nmero de unidades" defaultValue={this.state.row.stock} onChange={this.changeInputs.bind(this,"stock")}/>
                                    </InputGroup>
                                  </Col>
                                </Row>
                                <Row>
                                  <Col>
                                    <Label>Precio: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-barcode"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="number" id="price" placeholder="Ingrese el costo" required defaultValue={this.state.row.price} onChange={this.changeInputs.bind(this,"price")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Margen</Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="margin" placeholder="Ingrese la referencia" defaultValue={this.state.row.margin} onChange={this.changeInputs.bind(this,"margin")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Total</Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="total" placeholder="Ingrese la referencia" defaultValue={this.state.row.total} onChange={this.changeInputs.bind(this,"reference")}/>
                                    </InputGroup>
                                  </Col>
                                </Row>
                              </TabPane>
                            </TabContent>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="3">nada3</TabPane>
                            </TabContent>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="4">nada4</TabPane>
                            </TabContent>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        </Col>
      </Row>

    );
  }
}

const mapStateToProps = (state) => {
  return{
    obj:state.DaoProducts
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    }

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Eidts);
