import React, { Component } from 'react';
import {Button, Row, Col, Card, CardHeader, CardBody, Label, InputGroup, InputGroupAddon, InputGroupText, Input} from 'reactstrap';
import { connect } from 'react-redux';
import DbCrud from '../../../../Actions/DbCrud';

class Edit extends Component{

    constructor(props){
        super(props);
        this.info = this.info.bind(this);
    }

    componentDidMount(){
        this.props.updateConfigs("loadConfigs", "stores/configs/find");
    }

    saveProduct(){

    }

    info(obj){
        if(obj.configs[0]){
            return(
                <Row>
                    <Col>
                        <Label>Margen protecci√≥n</Label>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText><i className="fa fa-shield"></i></InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" id="protection" defaultValue={obj.configs[0].protection} placeholder="Ingrese el margen a proteger" />
                        </InputGroup>
                    </Col>
                    <Col>
                        <Label>Margen Max Protegido</Label>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText><i className="fa fa-shield"></i></InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" id="protection" defaultValue={obj.configs[0].maxMargin} placeholder="Ingrese el margen a proteger" />
                        </InputGroup>
                    </Col>
                    <Col>
                        <Label>Margen Incentivo</Label>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText><i className="fa fa-shield"></i></InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" id="protection" defaultValue={obj.configs[0].incentive} placeholder="Ingrese el margen a proteger" />
                        </InputGroup>
                    </Col>
                </Row>
            )
        }  
    }

    render(){
        return(
            <Row>
                <Col xs="12" md="12">
                    <Row>
                        <Col xs="12" md="12">
                            <Card>
                                <CardHeader>
                                    <Row>
                                        <Col xs="10" sm="10" md="10">Editar o Crear una nueva tienda</Col>
                                        <Col xs="2" sm="2" md="2">
                                            <Button size="sm" style={{fontSize:"medium"}} block className="float-right btn btn-success active">
                                                Guardar
                                            </Button>
                                        </Col>
                                    </Row>
                                </CardHeader>
                                <CardBody>
                                    {this.info(this.props.obj)}
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Col>
            </Row>
        )
    }

}


const mapStateToProps = (state) => {
    return{
      obj:state.DaoStores
    }
  }

  const mapDispatchToProps = (dispatch) => {
    return {
      updateConfigs: (type, colletion) => {
        DbCrud.dbMLoad(dispatch, type, colletion, {}, "POST");
      }
    }
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Edit);
