import React, {Component} from 'react';
import { connect } from 'react-redux';
import {Card, CardHeader, CardBody, Button, ButtonGroup} from 'reactstrap';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import { Icon } from 'semantic-ui-react';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import data from './_data';
import DbCrud from '../../../Pojo/DbCrud';

class List extends Component {
  constructor(props) {
    super(props);

    this.table = data.rows;
    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 3,
      hidePageListOnlyOnePage: true,
      clearSearch: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false
    }

  }

  componentDidMount(){
    // this.props.dispReducer('loadCompany',"email", this.props.email);
    this.props.dispReducer('loadGeo',"geo");
  }

  editFormatter(cell, row) {
    return(
        <ButtonGroup>
            <Button size="sm" onClick={()=>this.editObject(row)} className="btn-spotify btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
              <i className="icon-pencil"></i>
            </Button>
            <Button size="sm" onClick={()=>this.editObject(row)} className="btn-reddit btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
              <i className="icon-trash"></i>
            </Button>
        </ButtonGroup>
    )
}

loadStates(cell, row){
  return(
      <Button size="sm" onClick={()=>this.editObject(row)} className="btn-dropbox btn-brand text mr-1 mb-1 btn btn-secondary btn-lg">
        Ver Estados
      </Button>
  ) 
}

geopoint(cell, row){

}
active(){}

render() {

  return (
      <div className="animated">
        <Card>
          <CardHeader>
            <i className="icon-menu"></i>Lista de Geo
            <div className="card-header-actions">
              <a href="https://github.com/AllenFang/react-bootstrap-table" rel="noopener noreferrer" target="_blank" className="card-header-action">
                <small className="text-muted">Aprende</small>
              </a>
            </div>
          </CardHeader>
          <CardBody>
            <BootstrapTable data={this.props.obj} striped hover pagination search insertRow exportCSV>
              <TableHeaderColumn dataField='countryName' dataSort>Pais</TableHeaderColumn>
              <TableHeaderColumn isKey dataField='isoCode'>Iso Code</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.geopoint}>Geo Punto</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.active}>Activo</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.loadStates} dataAlign='right' >Estados</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.editFormatter} dataAlign='right' >Action</TableHeaderColumn>
            </BootstrapTable>
          </CardBody>
        </Card>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return{
    obj: state.DaoGeo
  }
}

const mapDispatchToProps = (dispatch) => {
  return{ 
    dispReducer: (type, colletion) => {
      DbCrud.dbFindColletion(dispatch, type, colletion);
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(List);
