import React, { Component } from 'react';
import { connect } from 'react-redux';
import { CSVLink, CSVDownload } from "react-csv";
import { Row,InputGroup, InputGroupAddon, InputGroupText, Form, FormGroup, Label, Input, Col, Card, CardHeader, CardBody, Modal, ModalHeader, ModalBody, ModalFooter, Button, ButtonGroup } from 'reactstrap';
import { AppSwitch } from '@coreui/react';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Pojo/DbCrud';
import Pagination from '../Pagination/Pagination';


var flat = true;

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class OrdersList extends Component {

  constructor(props) {
    super(props);

    this.state = {
      orders: {
        pag:"1",
        limit:"5",
        filFile:"",
        filValue: "",
        sorts:[
          {
            file:"oderDate",
            value:1
          }
        ]
      },
      groups : {
        filFile:"group",
        filValue:"StatusOrder",
        sorts:[{file:"name",value:1}]
      },
      brands : {
        filFile:"",
        filValue:"",
        sorts:[{file:"name",value:1}]
      },
      suppliers : {
        filFile:"",
        filValue:"",
        sorts:[{file:"name",value:1}]
      }

    }

    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 5,
      hidePageListOnlyOnePage: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false
    }
    this.pagLoad = this.pagLoad.bind(this);
    //this.toggleDanger = this.toggleDanger.bind(this);
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    if (flat) {
      flat = false;
      this.props.loadReducer('loadOrders', "orders/find",this.state.orders);
      this.props.loadReducer('loadStores', "stores/find",{});
      this.props.loadReducer('loadBrands', "brands/find",this.state.brands);
      this.props.loadReducer('loadGroups', "groups/find",this.state.groups);
      this.props.loadReducer('loadSuppliers', "suppliers/find",this.state.suppliers);
    }
  }

  //Cambia la paginacion
  pageChanged(e){
    //console.log(e.target.text)
  }

  //Funcion que permite hacer la llamada al formulario para editar un nuevo registro 
  callEdit(row) {
    this.props.history.push({
      pathname: '/Geo/CountriesAdd',
