import React, { Component } from 'react';
import { connect } from 'react-redux';
import { BreadcrumbItem, Breadcrumb, Row, Label, InputGroupAddon, InputGroup, InputGroupText, Col, Button, Input, Card, CardHeader, CardBody, ButtonGroup } from 'reactstrap';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Actions/DbCrud';
import { AppSwitch } from '@coreui/react';
import ReactQuill from 'react-quill';

import 'quill/dist/quill.snow.css';

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class Eidts extends Component {

  constructor(props) {
    super(props);
    this.state = {
       row : JSON.parse(localStorage.getItem("row")),
       text: ""
    }
    this.description = this.description.bind(this);
    this.sdescription = this.sdescription.bind(this);
    this.editor = {
      toolbar: [
        ['bold', 'italic', 'underline', 'strike'],        // toggled buttons
        ['blockquote', 'code-block'],
        [{ 'header': 1 }, { 'header': 2 }],               // custom button values
        [{ 'list': 'ordered'}, { 'list': 'bullet' }],
        [{ 'script': 'sub'}, { 'script': 'super' }],      // superscript/subscript
        [{ 'indent': '-1'}, { 'indent': '+1' }],          // outdent/indent
        [{ 'direction': 'rtl' }],                         // text direction
        [{ 'size': ['small', false, 'large', 'huge'] }],  // custom dropdown
        [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
        [{ 'color': [] }, { 'background': [] }],          // dropdown with defaults from theme
        [{ 'image': 1 }],
        [{ 'align': [] }],
        ['clean']                                         // remove formatting button
      ]
    }
    this.active = this.active.bind(this);
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
      console.log("ingrese a este componente");
    //this.props.loadReducer("loadCombinations", "combinations/", {});
  }

  active(cell){
    if(this.state.row.status){
        return(
            <AppSwitch className={'mx-1'} variant={'pill'} color={'success'} label checked/>
        );
      }else{
        return(
           <AppSwitch className={'mx-1'} variant={'pill'} color={'success'} label  />
        );
      }
  }

  saveLocalStorage(rowTmp){
    localStorage.setItem('row', JSON.stringify(rowTmp));
  }

  setDate(value){
    this.setState({row:value});
    this.saveLocalStorage(value);
  }

  description(value) {
    var rowTmp = this.state.row;
    rowTmp.description = value;
    this.setDate(rowTmp);
  }

  sdescription(value) {
      var rowTmp = this.state.row;
      rowTmp.sdescription = value;
      this.setDate(rowTmp);
  }

  render() {

    return (
      <Row>
        <Col xs="12" md="12">
            <Row>
                <Col xs="12" md="12">
                    <Card>
                        <CardHeader>
                            Editar o Crear un nuevo producto
                        </CardHeader>
                        <CardBody>
                            <Row>
                                <Col xs="4" sm="4" md="4">
                                    <Col className="pb-4">
                                        <Label>Codigo (EAN, SKU, UPC): </Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-barcode"></i></InputGroupText>
                                            </InputGroupAddon>
                                            <Input type="text" id="name" placeholder="Ingrese el código" required defaultValue={this.state.row.code}/>
                                        </InputGroup>
                                    </Col>
                                    <Col>
                                        <Label>Referencia</Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                            </InputGroupAddon>
                                            <Input type="text" id="name" placeholder="Ingrese la referencia" required defaultValue={this.state.row.reference}/>
                                        </InputGroup>
                                    </Col>
                                </Col>
                                <Col xs="4" sm="4" md="4">
                                    <Col className="pb-4">
                                        <Label>Nombre: </Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-edit"></i></InputGroupText>
                                            </InputGroupAddon>
                                            <Input type="text" id="name" placeholder="Ingrese el código" required defaultValue={this.state.row.name}/>
                                        </InputGroup>
                                    </Col>
                                    <Col className="pb-4">
                                        <Label>Marca: </Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-cubes"></i></InputGroupText>
                                            </InputGroupAddon>
                                            <Input type="text" id="name" placeholder="Ingrese el código" required defaultValue={this.state.row.brand}/>
                                        </InputGroup>
                                    </Col>
                                </Col>
                                <Col xs="4" sm="4" md="4">
                                    <Col className="pb-4">
                                        <Label>Estado: </Label>
                                        <InputGroup>
                                            {this.active()}
                                        </InputGroup>
                                    </Col>
                                    <Col className="pb-4">
                                        <Label>Vendedor: </Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-reddit-alien"></i></InputGroupText>
                                            </InputGroupAddon>
                                            <Input type="text" id="name" placeholder="Seleccione el Vendedor" required defaultValue={this.state.row.supplier}/>
                                        </InputGroup>
                                    </Col>
                                </Col>
                            </Row>

                            <Row>
                                <Col><ReactQuill value={() => {return (this.state.row.description == undefined)? "" : this.state.row.description}} modules={this.editor}  onChange={this.description}/></Col>
                                <Col><ReactQuill value={() => {return (this.state.row.sdescription == undefined)? "" : this.state.row.sdescription}} modules={this.editor} onChange={this.sdescription} /></Col>
                            </Row>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        </Col>
      </Row>

    );
  }
}

const mapStateToProps = (state) => {
  return{
    obj:state.DaoProducts
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    }

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Eidts);
