import firebase from './Firebase.js';
let db = firebase.firestore();


//Busca un registro puntual utilizando una llave o value fijo para en un campo o file 
function dbFindRegister(dispatch, type, colletion, file, value){
    db.collection(colletion).where(file, "==", value).get()
        .then(function(snapshot) {
            snapshot.forEach(function (doc) {
                dispatch({type:type,data:doc.data()})
            });
        }).catch(function(error) {
            console.log("Error getting documents: "+error);
    });
}

//Recupera todos los valores de una Collection de datos 
function dbFindColletion(dispatch, type, colletion){
    db.collection(colletion).get()
        .then(function(snapshot) {
            dispatch({type:type,data:snapshot});
        }).catch(function(error) {
            console.log("Error getting documents: "+error);
    });
}

//Crea un nuevo registro en la base de datos 
function dbCreate(dispatch, type, colletion, e){
    const dateRef = e.refs;
    const formData = {};
    for (const field in dateRef){
        if(dateRef[field].props.type == 'select')  formData[field] = document.getElementsByName(field)[0].value.replace(/\*/g,'');
        else formData[field] = dateRef[field].value.replace(/\*/g,'');
    }

    const southWest=new firebase.firestore.GeoPoint( formData["Longitud"], formData["Latitud"]);

    db.collection(colletion).add({
        active:formData['active']+"",
        countryName:formData['countryName']+"",
        geopoint: southWest,
        isoCode:formData['isoCode']+"",
        states:[]
    })
    .then(function(docRef) { console.log("Document written with ID: ", docRef.id);})
    .catch(function(error) { console.error("Error adding document: ", error); });

}


const DbCrud = {dbFindRegister, dbFindColletion, dbCreate}

export default DbCrud;
