import { connect } from 'react-redux';
import React, { Component } from 'react';
import DbCrud from '../../../Pojo/DbCrud';
import {
  Badge, Button, ButtonDropdown, Card, CardBody, CardFooter,
  CardHeader, Col, Collapse, DropdownItem, DropdownMenu,
  DropdownToggle, Fade, Form, FormGroup, FormText, FormFeedback,
  Input, InputGroup, InputGroupAddon, InputGroupText, Label, Row,
} from 'reactstrap';

import { TextMask, InputAdapter } from 'react-text-mask-hoc';


class MyAccount extends Component {

  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.toggleFade = this.toggleFade.bind(this);
    this.state = {
      collapse: true,
      fadeIn: true,
      timeout: 300
    };
  }

  toggle() {
    this.setState({ collapse: !this.state.collapse });
  }

  toggleFade() {
    this.setState((prevState) => { return { fadeIn: !prevState }});
  }

  callCountryList(){this.props.history.push('/Geo/CountriesList');}

  callSave(e){
    e.preventDefault();
    this.props.dispReducer('saveCountry', "geo", this );
  }

  render() {
    return (
      <div className="animated fadeIn">
        <Row>
          <Col xs="12" sm="12">
            <Card>
              <CardHeader>
                <strong>Crear nuevo Pa√≠s</strong>
                <Button color="secondary" onClick={this.callCountryList.bind(this)} className="float-right"><i className="fa icon-arrow-left-circle"></i>&nbsp;Regresar</Button>
              </CardHeader>
              <CardBody>

              <Form className="form-horizontal" onSubmit={this.callSave.bind(this)}>
                  <FormGroup row className="my-0">
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="countryName">Nombre Pais</Label>
                        <InputGroup >
                          <TextMask ref="countryName" id="countryName"  required autoComplete="off"
                            mask={[/[A-Z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,
                              /[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,
                              /[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/,
                              /[\sa-z]/,/[\sa-z]/,/[\sa-z]/,/[\sa-z]/]}
                            Component={InputAdapter}
                            placeholderChar = "*"
                            className="form-control" placeholder="Colombia"
                          />
                        </InputGroup>
                      </FormGroup>
                    </Col>
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="isoCode">Codigo ISO</Label>
                        <InputGroup >
                          <TextMask ref="isoCode" id="isoCode" required autoComplete="off"
                            mask={[/[A-Z]/,/[A-Z]/,/[A-Z]/]}
                            Component={InputAdapter}
                            placeholderChar = "*"
                            className="form-control" placeholder="COL"
                          />
                        </InputGroup>
                      </FormGroup>
                    </Col>
                  </FormGroup>

                  <FormGroup row className="my-0">
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="type">Estado</Label>
                        <Input type="select" name="active" ref="active" required >
                          <option value="1"  >Activo</option>
                          <option value="0" >Inactivo</option>
                        </Input> 
                      </FormGroup>
                    </Col>
                    <Col xs="3">
                      <FormGroup>
                        <Label htmlFor="Longitud">Geo Logitud</Label>
                        <TextMask ref="Longitud" id="Longitud" required autoComplete="off"
                            mask={[/[\-\+]/,/[1-9]/,/\d/,'.',/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,]}
                            Component={InputAdapter}
                            placeholderChar = "*"
                            className="form-control" placeholder="-12.2342323"
                          />
                      </FormGroup>
                    </Col>
                    <Col xs="3">
                      <FormGroup>
                        <Label htmlFor="Latitud">Geo Longitud</Label>
                        <TextMask ref="Latitud" id="Latitud" required autoComplete="off"
                            mask={[/[\-\+]/,/[1-9]/,/\d/,/\d/,'.',/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,/\d/,]}
                            Component={InputAdapter}
                            placeholderChar = "*"
                            className="form-control" placeholder="+23.234234"
                          />
                      </FormGroup>
                    </Col>
                  </FormGroup>
                      <div className="form-actions">
                        <Button color="primary" type="submit" value="Submit">Guardar</Button>&nbsp;&nbsp;
                        <Button color="secondary" onClick={this.callCountryList.bind(this)}>Cancel</Button>
                      </div>
                </Form>

              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
    return{
      email: state.login.email,
      password: state.login.password,
      obj: state.MyAccount
    }
}

const mapDispatchToProps = (dispatch) => {
    return{ 
      dispReducer: (type, colletion, e) => { 
        DbCrud.dbCreate(dispatch, type, colletion, e);
      } 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
