import React, { Component } from 'react';
import { Row, Col, Pagination, PaginationItem, PaginationLink, Input } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDoubleLeft, faAngleLeft, faAngleRight, faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons';


class ObjPag extends Component {

    constructor(props) {
        super(props);
    }

    pagRender(e,value){
        const RegPerFind = this.props.info.RegPerFind;
        const NumPages = this.props.info.NumPages;
        const limit = this.props.info.limit;
        const pag = this.props.info.pag;
        var rendPage = 0;
        rendPage = (NumPages < 10)? NumPages : 10;
        const temp = [];

        for (let i=1; i<=rendPage; i++) {
            if(i == pag){
                temp.push(
                    <PaginationItem key={"pag"+i} active>
                        <PaginationLink onClick={this.props.pagLoad.bind(this,""+i)}>
                            {i}
                        </PaginationLink>
                    </PaginationItem>
                )
            }else{
                temp.push(
                    <PaginationItem key={"pag"+i}>
                        <PaginationLink onClick={this.props.pagLoad.bind(this,""+i)}>
                            {i}
                        </PaginationLink>
                    </PaginationItem>
                )
            }
        }
        return temp;
    }

    render(){

        const propss = this.props.pagLoad.bind();

        return (
            <Row>
                <Col Col xs="2" md="2">
                    <Input type="select" defaultValue={this.props.info.limit}  key={"casa"}  name="select" id="pagNum" onChange={ 
                        (event) => {
                            const limit = event.target.value;
                            propss("cRegister",limit);
                        }
                    }> 
                        <option value='0'>{this.props.info.limit}</option>
                        <option value="5">5</option>
                        <option value="10" >10</option>
                        <option value="20" >20</option>
                        <option value="50" selected>50</option>
                        <option value="100" >100</option>       
                    </Input>
                </Col>
                <Col Col xs="8" md="8">
                    <Pagination  >
                        <PaginationItem key="allback">
                            <PaginationLink onClick={this.props.pagLoad.bind(this, 'allback')}>
                                <FontAwesomeIcon  icon={faAngleDoubleLeft} />
                            </PaginationLink>
                        </PaginationItem>
                        <PaginationItem key="back">
                            <PaginationLink previous onClick={this.props.pagLoad.bind(this, "back")}>
                                <FontAwesomeIcon  icon={faAngleLeft} />
                            </PaginationLink>
                        </PaginationItem>

                        {this.pagRender()}

                        <PaginationItem key="forward">
                            <PaginationLink onClick={this.props.pagLoad.bind(this, "forward")}>
                                <FontAwesomeIcon  icon={faAngleRight} />
                            </PaginationLink>
                        </PaginationItem>
                        <PaginationItem key="allforward">
                            <PaginationLink previous onClick={this.props.pagLoad.bind(this, "allforward")}>
                                <FontAwesomeIcon  icon={faAngleDoubleRight} />
                            </PaginationLink>
                        </PaginationItem>
                    </Pagination>
                </Col>
            </Row>

        );
    }
}

export default ObjPag;
