import React, { Component } from 'react';
import { connect } from 'react-redux';
import { CSVLink } from "react-csv";
import { Row,InputGroup, InputGroupAddon, InputGroupText, Form, FormGroup, Input, Col, Card, CardHeader, CardBody, Button, ButtonGroup } from 'reactstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import { AppSwitch } from '@coreui/react'
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Actions/DbCrud';
import Pagination from '../Pagination/Pagination';
import CurrencyFormat from 'react-currency-format';


var flat = true;

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class List extends Component {

  constructor(props) {
    super(props);
    this.state = {
      filters: {
        pag:"1",
        limit:"5",
        filFile:"",
        filValue: "",
        sorts:[
          {
            file:"cDate",
            value:1
          }
        ]
      }
    }

    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 5,
      hidePageListOnlyOnePage: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false
    }

    this.pagLoad = this.pagLoad.bind(this);
    //this.toggleDanger = this.toggleDanger.bind(this);
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    //console.log(this.props);
    //console.log(this.store);
    if (flat) {
      flat = false;
      //Typo: para ser asignado los valores al tener una respuesta del servidor 
      //EndPoint: al que tenemos que llamar para recupuerar datos
      //Obj Filtro: el objeto que permite realizar una filto en la busqueda
      this.props.loadReducer('loadProducts', "products/find",this.state.filters);
      //this.props.loadReducer('loadFilters', "orders/filters",{});
    }
  } 

  //Funcion que permite hacer la llamada al formulario para editar un nuevo registro 
  callEdit(row) {
    this.props.history.push({
      pathname: '/Orders/Edit/'+row.order+'/'+row.reference
    })
  }

  //Cambia el valor a visible del mensaje de alerta 
  //guarda el identificador de la fila que sera afectada 
  toggleDanger(row) {
    this.setState({
      danger: !this.state.danger,
      tmpRow: row.order
    });
  }

  // Esta funcion escucha los eventos del componente Pagination
  pagLoad(pagNum,limit) {
      //Se realiza la solicitud nuevamente para recargar los registros 
      this.props.loadReducer('loadProducts', "products/find", {
        pag:pagNum,
        limit:limit,
        filFile:this.state.filters.filFile,
        filValue:this.state.filters.filValue,
        sorts:this.state.filters.sorts
      });

      this.setState({
        orders:{
          pag:pagNum,
          limit:limit,
          filFile:this.state.filters.filFile,
          filValue:this.state.filters.filValue,
          sorts:this.state.filters.sorts
        }
      });

  }

  onClickForm(idInput){
    var infoTmp = document.getElementById(idInput).value;
    if(infoTmp == "0") infoTmp = "";

    //Se realiza la solicitud nuevamente para recargar los registros 
    this.props.loadReducer('loadOrders', "orders/find", {
      pag:1,
      limit:5,
      filFile:idInput,
      filValue:infoTmp,
      sorts:this.state.filters.sorts
    });

    //Actualizamos el esta con los valores de filtros seleccionados esto por si se utiliza la 
    //paginacion por lo que se requiere que el estado mantenga el filtro de campo 
    this.setState({
        filters:{
          pag:1,
          limit:5,
          filFile:idInput,
          filValue:infoTmp,
          sorts:this.state.filters.sorts
        }
      }
    );
  }

  //resibe 
  renderPagination(){
    if(this.props.obj.pagination.NumPages != undefined)
    return <Pagination info={this.props.obj.pagination} pagLoad={this.pagLoad.bind()}/>
    return (<div>Pag...</div>);
  }

  //Funcion que construye los botones de ediccion para cada registro listado en la tabla 
  editFormatter(cell, row) {
    return (
      <ButtonGroup>
        <Button onClick={this.callEdit.bind(this, row)} size="sm" className="btn-spotify btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
          <i className="icon-pencil"></i>
        </Button>
        <Button onClick={this.toggleDanger.bind(this, row)} size="sm" className="btn-reddit btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
          <i className="icon-trash"></i>
        </Button>
      </ButtonGroup>
    )
  }

  editCost(cell, row){return this.CurrencyFormat(row.cost);}
  editPrice(cell, row){return this.CurrencyFormat(row.price);}
  editTotal(cell, row){return this.CurrencyFormat(row.total);}
  CurrencyFormat(value){
    return (
      <CurrencyFormat value={value} displayType={'text'} thousandSeparator={true} decimalSeparator={""} decimalScale={false} prefix={'$'} renderText={value => value} />
    )
  }

  active(cell, row){
    if(row.status == '1'){
        return(
            <AppSwitch className={'mx-1'} variant={'pill'} color={'success'} label checked/>
        );
      }else{
        return(
           <AppSwitch className={'mx-1'} variant={'pill'} color={'success'} label  />
        );
      }
  }

  filters(filter){
    /*
    const tmpData = this.props.obj.listFilters;
    const obj = [];

    Object.keys(tmpData).forEach(function(key){
      if(key == filter){
        for(var i=0; i<tmpData[key].length; i++){
          var keys = key+""+i;
          if(i==0) obj.push(<option key={keys} value="0">{tmpData[key][i]}</option>)
          else obj.push(<option key={keys} value={tmpData[key][i]}>{tmpData[key][i]}</option>)
        }
      }
    })
    return obj;
    */
  }

  render() {
    return (
      <div className="animated">
        <Card>
          <CardHeader>
            Lista Productos
            <div className="card-header-actions">
              <CSVLink data={this.props.obj.listProducts}>Descargar Busqueda</CSVLink>
            </div>
          </CardHeader>
          <CardBody>
            <Row>
              <Col xs="12" md="12">
                <div className="pull-right">{this.renderPagination()}</div>
              </Col>
            </Row>
          </CardBody>

          <CardBody>
              <BootstrapTable  data={this.props.obj.listProducts} striped hover options={this.options} search multiColumnSearch strictSearch > 
              <TableHeaderColumn width={'8%'} dataField='reference' >Referencia</TableHeaderColumn>
              <TableHeaderColumn width={'12%'} dataField='code' >Codigo</TableHeaderColumn>
              <TableHeaderColumn width={'20%'} dataField='name' >Nombre</TableHeaderColumn>
              <TableHeaderColumn width={'8%'} dataField='brand' >Marca</TableHeaderColumn>
              <TableHeaderColumn width={'9%'} dataField='supplier' >Proveedor</TableHeaderColumn>
              <TableHeaderColumn width={'4%'} dataField='tax' >Tax</TableHeaderColumn>
              <TableHeaderColumn width={'8%'} dataFormat={this.editCost.bind(this)} dataAlign='right'>Costo</TableHeaderColumn>
              <TableHeaderColumn width={'8%'} dataFormat={this.editPrice.bind(this)} dataAlign='right'>Precio</TableHeaderColumn>
              <TableHeaderColumn width={'8%'} dataFormat={this.editTotal.bind(this)} dataAlign='right'>Total</TableHeaderColumn>
              <TableHeaderColumn width={'8%'} dataFormat={this.active.bind(this)} dataAlign='center'>Estado</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.editFormatter.bind(this)} width={'8%'} dataAlign='center' >Accion</TableHeaderColumn>
              <TableHeaderColumn isKey={true} dataField='_id' hidden>Identifier ID</TableHeaderColumn>
            </BootstrapTable>
          </CardBody>

          <CardBody>
            <Row>
              <Col xs="12" md="12">
                <div className="pull-right">{this.renderPagination()}</div>
              </Col>
            </Row>
          </CardBody>

        </Card>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  //Registra el objeto la funcion que cargara el objeto en el estado
  return{
    obj:state.DaoOrders
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    }

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(List);
