import React, { Component } from 'react';
import { connect } from 'react-redux';
import DbCrud from '../../../Actions/DbCrud';
import {Modal, ButtonGroup, Button, FormGroup, Row, Label, InputGroupAddon, InputGroup, InputGroupText, Col,  Input, Card, CardHeader, CardBody, Nav, NavItem, NavLink, TabContent, TabPane } from 'reactstrap';
import ReactQuill from 'react-quill';
import 'quill/dist/quill.snow.css';

// React select
import Select from 'react-select';
import 'react-select/dist/react-select.min.css';
import { parseFloat } from 'core-js/fn/number';

//Alerts
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Enumeradores
import banks from './Banks';
const pse = banks.PSE;


/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class Eidts extends Component {
    constructor(props){
        super(props);
        this.state = {
            selPse:[],
            row:JSON.parse(localStorage.getItem("row"))
        }
        this.editor = {
            toolbar: [
              ['bold', 'italic', 'underline', 'strike'],        // toggled buttons
              ['blockquote', 'code-block'],
              [{ 'header': 1 }, { 'header': 2 }],               // custom button values
              [{ 'list': 'ordered'}, { 'list': 'bullet' }],
              [{ 'script': 'sub'}, { 'script': 'super' }],      // superscript/subscript
              [{ 'indent': '-1'}, { 'indent': '+1' }],          // outdent/indent
              [{ 'direction': 'rtl' }],                         // text direction
              [{ 'size': ['small', false, 'large', 'huge'] }],  // custom dropdown
              [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
              [{ 'color': [] }, { 'background': [] }],          // dropdown with defaults from theme
              [{ 'image': 1 }],
              [{ 'align': [] }],
              ['clean']                                         // remove formatting button
            ]
          }
          this.addStore = this.addStore.bind(this);
    }

    componentDidMount(){
        console.log(this.state.row);
    }

    description(value) {
        var rowTmp = this.state.row;
        rowTmp.description = value;
        localStorage.setItem('row', JSON.stringify(rowTmp));
    }

    saveProduct(){}

    changeInputs(idInput){
        var rowTmp = this.state.row;
        var value = document.getElementById(idInput).value;
        if(idInput == "name") rowTmp.name = value;
        localStorage.setItem('row', JSON.stringify(rowTmp));
    }

    addStore(obj){
        this.setState({selPse:obj});
    }

    render(){
        return(
            <Row>
                <ToastContainer position="top-right" autoClose={9000} style={{zIndex:"1999"}}/>
                <Col xs="12" md="12">
                    <Row>
                        <Col xs="12" md="12">
                            <Card>
                                <CardHeader>
                                <Row>
                                    <Col xs="10" sm="10" md="10">Editar o Crear una nueva tienda</Col>
                                    <Col xs="2" sm="2" md="2">
                                        <Button size="sm" style={{fontSize:"medium"}} onClick={this.saveProduct.bind(this)} block className="float-right btn btn-success active">
                                            Guardar Tienda
                                        </Button>
                                    </Col>
                                </Row>
                                </CardHeader>
                                <CardBody>
                                    <Row className="pb-3">
                                        <Col>
                                            <Label>Nombre</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-user-circle"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="name" placeholder="Ingrese el nombre" defaultValue={this.state.row.name} onChange={this.changeInputs.bind(this,"name")}/>
                                            </InputGroup>
                                        </Col>
                                        <Col>
                                            <Label>Invitacion</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-envelope-open"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="invitation" placeholder="Ingrese la invitacion" defaultValue={this.state.row.invitation} onChange={this.changeInputs.bind(this,"invitation")}/>
                                            </InputGroup>
                                        </Col>
                                        <Col>
                                            <Label>Fee</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-money"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="fee" placeholder="Ingrese el margen" defaultValue={this.state.row.fee} onChange={this.changeInputs.bind(this,"fee")}/>
                                            </InputGroup>
                                        </Col>
                                        <Col>
                                            <Label>Fee Pago</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-credit-card"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="feePay" placeholder="Ingrese el margen" defaultValue={this.state.row.feePay} onChange={this.changeInputs.bind(this,"feePay")}/>
                                            </InputGroup>
                                        </Col>
                                    </Row>
                                    <Row className="pb-3">
                                        <Col>
                                            <Label>Descuento</Label>
                                                <InputGroup>
                                                    <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-cart-arrow-down"></i></InputGroupText>
                                                    </InputGroupAddon>
                                                <Input type="text" id="discount" placeholder="Ingrese el margen" defaultValue={this.state.row.discount} onChange={this.changeInputs.bind(this,"discount")}/>
                                            </InputGroup>
                                        </Col>
                                        <Col>
                                            <Label>Bins</Label>
                                            <InputGroup>
                                                <InputGroupAddon addonType="prepend">
                                                    <InputGroupText><i className="fa fa-barcode"></i></InputGroupText>
                                                </InputGroupAddon>
                                                <Input type="text" id="bins" placeholder="Ingrese la invitacion" defaultValue={this.state.row.bins} onChange={this.changeInputs.bind(this,"bins")}/>
                                            </InputGroup>
                                        </Col>
                                        <Col>
                                            <Label>PSE</Label>
                                            <Select 
                                                name="stores" 
                                                value={this.state.selPse} 
                                                options={pse} 
                                                onChange={this.addStore} multi 
                                            />
                                        </Col>
                                        <Col>
                                            <Label>Email</Label>
                                                <InputGroup>
