import React, { Component } from 'react';
import { createStore, combineReducers, applyMiddleware } from 'redux';
import { Provider } from 'react-redux';
import { connect } from 'react-redux';
import thunk from 'redux-thunk'
import promise from "redux-promise-middleware";
import { Redirect, HashRouter as Router, Switch } from 'react-router-dom';
import { Container } from 'reactstrap';
import Login from '../views/Pages/Login/Login';
import DaoLogin from './DaoLogin';
import DaoMyAccount from './DaoMyAcoount';
import DaoGeo from './DaoGeo';
import DaoOrders from './DaoOrders';
import firebase from '../Actions/Firebase.js';
import 'firebase/firestore';

import {AppAside, AppBreadcrumb, AppFooter, AppHeader, AppSidebar, AppSidebarFooter,
  AppSidebarForm, AppSidebarHeader, AppSidebarMinimizer, AppSidebarNav,} from '@coreui/react';

// sidebar nav config
import navigation from '../_nav';
// routes config
import routes from '../routes';

import DefaultAside from '../containers/DefaultLayout/DefaultAside';
import DefaultFooter from '../containers/DefaultLayout/DefaultFooter';
import DefaultHeader from '../containers/DefaultLayout/DefaultHeader';
import { match } from 'core-js/fn/symbol';

//Utilizamos el combineReducers con el fin de administrar los estados, de esta manera cada estado tiene asociado  
//una funcion que se encargara de su adminsitraci√≥n 
const reducer = combineReducers({
    login: DaoLogin.reducerLogin,
    MyAccount: DaoMyAccount.MyAccount,
    DaoGeo: DaoGeo.Geo,
    DaoOrders: DaoOrders.Orders
});

//Donde almacenamos nuestro estado
const store = createStore(reducer, applyMiddleware(thunk, promise));

function DaoIni(props) {

        return (
            <div className="app">
             <AppHeader fixed>
             <DefaultHeader />
             </AppHeader>
                 <div className="app-body">
             <AppSidebar fixed display="lg">
                 <AppSidebarHeader />
                 <AppSidebarForm />
                 <AppSidebarNav navConfig={navigation} {...props} />
                 <AppSidebarFooter />
                 <AppSidebarMinimizer />
             </AppSidebar>
             <main className="main">
                 <AppBreadcrumb appRoutes={routes}/>
                 <Container fluid>
                 <Switch>
                     {routes.map(
                       (route, idx) => {
                         return route.component ? (
                         <Route 
                             key={idx} 
                             path={route.path} 
                             exact={route.exact} 
                             name={route.name} 
                             render={props => (<route.component {...props} />)} 
                         />)
                         : (null);
                     },
                     )}
                 </Switch>
                 </Container>
             </main>
             <AppAside fixed>
                 <DefaultAside />
             </AppAside>
             </div>
                 <AppFooter>
             <DefaultFooter />
             </AppFooter>
             </div>
        )

}



class Dao extends Component {

    constructor(props){
        super(props);
        this.state = {
            email:""
        }
    }

    componentDidMount () {
        const userData = []
        firebase.auth().onAuthStateChanged(user => {
            if (user) {
                userData["email"] = user.email;
                //store.dispatch({type:"loadUser",user});
                console.log("Que carajo");
                if(window.location.hash ==  "#/login" || window.location.hash ==  "#/" ) window.location = "#/dashboard";
                document.getElementById('root').style.visibility = 'visible';
                this.setState({email:user.email})
            } else {
                if(window.location.hash !=  "#/login" || window.location.hash ==  "#/") window.location = "#/login";
                document.getElementById('root').style.visibility = 'visible';
            }
        });
    }

  render() {
    console.log(this.state);
    if(window.location.hash == "#/login" && this.state.email == ""){
        return(
            <Provider store={store}>
                <Switch>
                    <HashRouter name="Login Page" component={Login} />
                </Switch>
            </Provider>
        )
    }else{
        return (
            <Provider store={store}>
                <DaoIni {...this.props} userData={this.state}/>
            </Provider>
        )
    }
  }
}

export default Dao;
