import React, { Component } from 'react';
import { createStore, combineReducers, pplyMiddleware, applyMiddleware } from 'redux';
import { Provider } from 'react-redux';
import ReduxThunk from 'redux-thunk'
import { Redirect, Route, Switch } from 'react-router-dom';
import { Container } from 'reactstrap';
import Login from '../views/Pages/Login/';
import DaoLogin from './DaoLogin';
import firebase from '../Pojo/Firebase.js';
import 'firebase/firestore';

import {AppAside, AppBreadcrumb, AppFooter, AppHeader, AppSidebar, AppSidebarFooter,
  AppSidebarForm, AppSidebarHeader, AppSidebarMinimizer, AppSidebarNav,} from '@coreui/react';

// sidebar nav config
import navigation from '../_nav';
// routes config
import routes from '../routes';

import DefaultAside from '../containers/DefaultLayout/DefaultAside';
import DefaultFooter from '../containers/DefaultLayout/DefaultFooter';
import DefaultHeader from '../containers/DefaultLayout/DefaultHeader';

//Utilizamos el combineReducers con el fin de administrar los estados, de esta manera cada estado tiene asociado  
//una funcion que se encargara de su adminsitración 
const reducer = combineReducers({
    login: DaoLogin.reducerLogin,
    tareas: DaoLogin.reducerTareas,
    //userAdd: DaoUser.userAdd
});

//Donde almacenamos nuestro estado
const store = createStore(reducer, applyMiddleware(ReduxThunk));

function DaoIni(props) {
    document.getElementById('root').style.visibility = 'hidden';
    firebase.auth().onAuthStateChanged(function(user) {
        if (user) {
            var displayName = user.displayName;
            var email = user.email;
            var emailVerified = user.emailVerified;
            var photoURL = user.photoURL;
            var isAnonymous = user.isAnonymous;
            var uid = user.uid;
            var providerData = user.providerData;
            // console.log(displayName+"   --  "+email+"   --  "+emailVerified+"   --  "+photoURL+"   --  "+photoURL+"   --  "+isAnonymous+"   --  "+uid+"   --  "+providerData)
            console.log(window.location.hash);
            if(window.location.hash ==  "#/login" || window.location.hash ==  "#/" ) window.location = "#/dashboard";
            document.getElementById('root').style.visibility = 'visible';
            store.dispatch({type:"loadUser",user})
        } else {
            if(window.location.hash !=  "#/login" || window.location.hash ==  "#/") window.location = "#/login";
            document.getElementById('root').style.visibility = 'visible';
        }

    });

    if(window.location.hash == "#/login"){
        return(
            <Switch>
                <Route name="Login Page" component={Login} />
            </Switch>
        )
    }
    else{ 
        return (
           /* <div className="app">
                <AppHeader fixed>
            <DefaultHeader />
            </AppHeader>
                <div className="app-body">
            <AppSidebar fixed display="lg">
                <AppSidebarHeader />
                <AppSidebarForm />
                <AppSidebarNav navConfig={navigation} {...props} />
                <AppSidebarFooter />
                <AppSidebarMinimizer />
            </AppSidebar>
            <main className="main">
                <AppBreadcrumb appRoutes={routes}/>
                <Container fluid>
                <Switch>
                    {routes.map((route, idx) => {
                        return route.component ? (<Route key={idx} path={route.path} exact={route.exact} name={route.name} render={props => (
                            <route.component {...props} />
                        )} />)
                        : (null);
                    },
                    )}
                    <Redirect from="/" to="/dashboard" />
                </Switch>
                </Container>
            </main>
            <AppAside fixed>
                <DefaultAside />
            </AppAside>
            </div>
                <AppFooter>
            <DefaultFooter />
            </AppFooter>
            </div>
        */
       <div>hola como estas</div>
       )
    }
    return (<div>hola como estas</div>)
}

class Dao extends Component {
  render() {
    return (
        <Provider store={store}>
            <DaoIni {...this.props}/>
        </Provider>
      );
  }
}

export default Dao;
