import React, { Component } from 'react';
import { connect } from 'react-redux';
import DbCrud from '../../../Actions/DbCrud';
import { AppSwitch } from '@coreui/react';
import {Modal, Button, Row, Label, InputGroupAddon, InputGroup, InputGroupText, Col,  Input, Card, CardHeader, CardBody} from 'reactstrap';
import ReactQuill from 'react-quill';
import 'quill/dist/quill.snow.css';
import { CommonLoading } from 'react-loadingg';

//Alerts
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class Eidts extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loader:false,
            selPse:[{value:"all", label:"Todas", disable:true}],
            row:JSON.parse(localStorage.getItem("row")),
        }

        this.description = this.description.bind(this);
        this.optionPse = this.optionPse.bind(this);
        this.optionStore = this.optionStore.bind(this);
        this.price = this.price.bind(this);
        this.callR = this.callR.bind(this);
        this.selectUsers = this.selectUsers.bind(this);
        this.editor = {
            toolbar: [
              ['bold', 'italic', 'underline', 'strike'],        // toggled buttons
              ['blockquote', 'code-block'],
              [{ 'header': 1 }, { 'header': 2 }],               // custom button values
              [{ 'list': 'ordered'}, { 'list': 'bullet' }],
              [{ 'script': 'sub'}, { 'script': 'super' }],      // superscript/subscript
              [{ 'indent': '-1'}, { 'indent': '+1' }],          // outdent/indent
              [{ 'direction': 'rtl' }],                         // text direction
              [{ 'size': ['small', false, 'large', 'huge'] }],  // custom dropdown
              [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
              [{ 'color': [] }, { 'background': [] }],          // dropdown with defaults from theme
              [{ 'image': 1 }],
              [{ 'align': [] }],
              ['clean']                                         // remove formatting button
            ]
        }  
    }

    componentDidMount() {
        this.props.findAdviser();
    }

    description(value) {
        var rowTmp = this.state.row;
        rowTmp.description = value;
        localStorage.setItem('row', JSON.stringify(rowTmp));
    }

    saveProduct(){
        console.log(this.state.row.fee);
        if(this.state.row.name == '') toast.error('El nombre de la tienda no puede ser nula.');
        else if(this.state.row.invitation == ''){
            if(!this.state.row.catalogue) toast.error('La invitaciÃ³n solo puede ser nula para un catalogo.');
        }
        else if(this.state.row.fee >= 0) toast.error('El Fee requiere un valor de 0 o mas.');
        else if(this.state.row.feePay >= 0) toast.error('El feePay requiere un valor de 0 o mas.');
        else if(this.state.row.discount >= 0 ) toast.error('Se requiere un valor de 0 o mas.');
        else if(this.state.row.bins == '') toast.error('Si no hya bins dejar el valor en 0.');

        //this.setState({loader:true});
        //this.props.loadReducer('updateStores', "stores/update", this.state.row, this.callR);
    }

    callR(){
        this.setState({loader:false});
    }

    changeInputs(idInput){
        var rowTmp = this.state.row;
        var value = document.getElementById(idInput).value;
        if(idInput == "name") rowTmp.name = value;
        if(idInput == "invitation") rowTmp.invitation = value;
        if(idInput == "fee") rowTmp.fee = value;
        if(idInput == "feePay") rowTmp.feePay = value;
        if(idInput == "discount") rowTmp.discount = value;
        if(idInput == "bins") rowTmp.bins = value;
        if(idInput == "catalogue") rowTmp.catalogue = document.getElementById(idInput).checked;
        if(idInput == "viewPrice") rowTmp.viewPrice = document.getElementById(idInput).checked;
        if(idInput == "bank") rowTmp.bank = value;
        if(idInput == "typeStore") rowTmp.typeStore = value;
        localStorage.setItem('row', JSON.stringify(rowTmp));
        this.setState({row:rowTmp});
    }

    catalogue(cell){
        if(this.state.row.catalogue){
            return(
                <AppSwitch id="catalogue" onChange={this.changeInputs.bind(this,"catalogue")} className={'mx-1'} variant={'pill'} color={'success'} label checked/>
            );
        }else{
            return(
               <AppSwitch id="catalogue" onChange={this.changeInputs.bind(this,"catalogue")} className={'mx-1'} variant={'pill'} color={'success'} label  />
            );
        }
    }

    price(cell){
        if(this.state.row.viewPrice){
            return(
                <AppSwitch id="viewPrice" onChange={this.changeInputs.bind(this,"viewPrice")} className={'mx-1'} variant={'pill'} color={'success'} label checked/>
            );
        }else{
            return(
               <AppSwitch id="viewPrice" onChange={this.changeInputs.bind(this,"viewPrice")} className={'mx-1'} variant={'pill'} color={'success'} label  />
            );
        }
    }

    optionStore(){
        var option = [];
        if(this.state.row.typeStore == '1') option.unshift(<option key='Store_1' value='1'>Banco</option>);
        else option.push(<option key='Store_1' value='1'>Banco</option>);

        if(this.state.row.typeStore == '2') option.unshift(<option key='Store_2' value='2'>Fondo</option>);
        else option.push(<option key='Store_2' value='2'>Fondo</option>);

        if(this.state.row.typeStore == '3') option.unshift(<option key='Store_3' value='3'>Empresa</option>);
        else option.push(<option key='Store_3' value='3'>Empresa</option>);

        if(this.state.row.typeStore == '4') option.unshift(<option key='Store_4' value='4'>Proveedor</option>);
        else option.push(<option key='Store_4' value='4'>Proveedor</option>);

        return(
            <Input type="select" id='typeStore' name='typeStore' key='typeStore' onChange={this.changeInputs.bind(this,'typeStore')} >
                {option}
            </Input>
        )
    }

    optionPse(){
        var option = [];
        if(this.state.row.bank == '0') option.unshift(<option key='Bank_0' value='0'>TODOS</option>);
        else option.push(<option key='Bank_0' value='0'>TODOS</option>);

        if(this.state.row.bank == '1') option.unshift(<option key='Bank_1' value='1'>BANCO AGRARIO</option>);
        else option.push(<option key='Bank_1' value='1'>BANCO AGRARIO</option>);

        if(this.state.row.bank == '2')  option.unshift(<option key='Bank_2' value='2'>BANCO AV VILLAS</option>);
        else option.push(<option key='Bank_2' value='2'>BANCO AV VILLAS</option>);

        if(this.state.row.bank == '3') option.unshift(<option key='Bank_3' value='3'>BANCO CAJA SOCIAL</option>);
        else option.push(<option key='Bank_3' value='3'>BANCO CAJA SOCIAL</option>);

        if(this.state.row.bank == '4') option.unshift(<option key='Bank_4' value='4'>BANCO COLPATRIA</option>);
        else option.push(<option key='Bank_4' value='4'>BANCO COLPATRIA</option>);

        if(this.state.row.bank == '5') option.unshift(<option key='Bank_5' value='5'>BANCO DAVIVIENDA</option>);
        else option.push(<option key='Bank_5' value='5'>BANCO DAVIVIENDA</option>);

        if(this.state.row.bank == '6') option.unshift(<option key='Bank_6' value='6'>BANCO DE BOGOTA</option>);
        else option.push(<option key='Bank_6' value='6'>BANCO DE BOGOTA</option>);

        if(this.state.row.bank == '7') option.unshift(<option key='Bank_7' value='7'>BANCO DE OCCIDENTE</option>);
        else option.push(<option key='Bank_7' value='7'>BANCO DE OCCIDENTE</option>);

        if(this.state.row.bank == '8') option.unshift(<option key='Bank_8' value='8'>BANCO GNB SUDAMERIS</option>);
        else option.push(<option key='Bank_8' value='8'>BANCO GNB SUDAMERIS</option>);

        if(this.state.row.bank == '9') option.unshift(<option key='Bank_9' value='9'>BANCO PICHINCHA</option>);
        else option.push(<option key='Bank_9' value='9'>BANCO PICHINCHA</option>);

        if(this.state.row.bank == '10') option.unshift(<option key='Bank_10' value='10'>BANCO POPULAR</option>);
        else option.push(<option key='Bank_10' value='10'>BANCO POPULAR</option>);

        if(this.state.row.bank == '11') option.unshift(<option key='Bank_11' value='11'>BANCO PROCREDIT</option>);
        else option.push(<option key='Bank_11' value='11'>BANCO PROCREDIT</option>);

        if(this.state.row.bank == '12') option.unshift(<option key='Bank_12' value='12'>BANCOLOMBIA</option>);
        else option.push(<option key='Bank_12' value='12'>BANCOLOMBIA</option>);

        if(this.state.row.bank == '13') option.unshift(<option key='Bank_13' value='13'>BANCOOMEVA</option>);
        else option.push(<option key='Bank_13' value='13'>BANCOOMEVA</option>);

        if(this.state.row.bank == '14') option.unshift(<option key='Bank_14' value='14'>BBVA COLOMBIA</option>);
        else option.push(<option key='Bank_14' value='14'>BBVA COLOMBIA</option>);

        if(this.state.row.bank == '15') option.unshift(<option key='Bank_15' value='15'>CITIBANK</option>);
        else option.push(<option key='Bank_15' value='15'>CITIBANK</option>);

        if(this.state.row.bank == '16') option.unshift(<option key='Bank_16' value='16'>ITAÃ</option>);
        else option.push(<option key='Bank_16' value='16'>ITAÃ</option>);

        if(this.state.row.bank == '17') option.push(<option key='Bank_17' value='17'>BANCO FALABELLA</option>);
        else  option.push(<option key='Bank_17' value='17'>BANCO FALABELLA</option>);

        return(
            <Input type="select" id='bank' name='bank' key='bank' onChange={this.changeInputs.bind(this,'bank')} >
                {option}
            </Input>
        )
    }

    selectUsers(obj){
        var option = [];
        option.push(<option key="user_0" value="user_0">Seleccione un asesor</option>);
        obj.forEach(val => {
            option.push(<option key={val._id} value={val._id}>{val.name}</option>);
        })
        return(
            <Input type="select" id='typeStore' name='typeStore' key='typeStore' onChange={this.changeInputs.bind(this,'typeStore')} >
                {option}
            </Input>
        )
    }

    render(){

