import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Container, CardImg, FormGroup, Row, Label, InputGroupAddon, InputGroup, InputGroupText, Col,  Input, Card, CardHeader, CardBody, Nav, NavItem, NavLink, TabContent, TabPane } from 'reactstrap';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Actions/DbCrud';
import { AppSwitch } from '@coreui/react';
import ReactQuill from 'react-quill';
import classnames from 'classnames';
import 'quill/dist/quill.snow.css';
import "./Products.css";
import CurrencyFormat from 'react-currency-format';

// React DateRangePicker
import 'react-dates/initialize';
import { DateRangePicker } from 'react-dates';
import 'react-dates/lib/css/_datepicker.css';
import "moment/locale/es";
import moment from "moment";

// React select
import Select from 'react-select';
import 'react-select/dist/react-select.min.css';



/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class Eidts extends Component {

  constructor(props) {
    super(props);
    this.state = {
       startDate:null, 
       endDate:null,
       selectStores:[],
       arrayStores:[],
       costBob: 0,
       totalTax:0,
       activeTab: '1',
       margTotalBob:0,
       margValSupplier: 0,
       totalDollar:0,
       row : JSON.parse(localStorage.getItem("row")),
       text: ""
    }
    this.toggle = this.toggle.bind(this);
    this.categories = this.categories.bind(this)
    this.description = this.description.bind(this);
    this.sdescription = this.sdescription.bind(this);
    this.selects = this.selects.bind(this);
    this.selectRolSupplier = this.selectRolSupplier.bind(this);
    this.addDates = this.addDates.bind(this);
    this.onFocusChange = this.onFocusChange.bind(this);
    this.addStore = this.addStore.bind(this);
    this.editor = {
      toolbar: [
        ['bold', 'italic', 'underline', 'strike'],        // toggled buttons
        ['blockquote', 'code-block'],
        [{ 'header': 1 }, { 'header': 2 }],               // custom button values
        [{ 'list': 'ordered'}, { 'list': 'bullet' }],
        [{ 'script': 'sub'}, { 'script': 'super' }],      // superscript/subscript
        [{ 'indent': '-1'}, { 'indent': '+1' }],          // outdent/indent
        [{ 'direction': 'rtl' }],                         // text direction
        [{ 'size': ['small', false, 'large', 'huge'] }],  // custom dropdown
        [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
        [{ 'color': [] }, { 'background': [] }],          // dropdown with defaults from theme
        [{ 'image': 1 }],
        [{ 'align': [] }],
        ['clean']                                         // remove formatting button
      ]
    }
  }

  toggle(tab) {
    localStorage.setItem('activeTab',tab);
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab,
      });
    }
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    this.calculatePrices();
    var rowTmp = this.state.row;
    if(this.state.row.description == undefined) rowTmp.description = "";
    if(this.state.row.sdescription == undefined) rowTmp.sdescription = "";
    this.setDate(rowTmp);
    this.setStores();
    this.setDate();
  }

  calculatePrices(){
    var idTax = this.state.row.idTax;
    var idSupplier = this.state.row.idSupplier;
    var pvp = this.state.row.pvp;
    var listTaxes = JSON.parse(localStorage.getItem("listTaxes"));
    var listSuppliers = JSON.parse(localStorage.getItem("listSuppliers"));
    var i = 0;
    for(i = 0; i< listTaxes.length; i++)  if(listTaxes[i]._id == idTax) break;
    var taxValue = (listTaxes[i].value/100)+1;

    for(i = 0; i< listSuppliers.length; i++)  if(listSuppliers[i]._id == idSupplier) break;
    var margValSupplier = listSuppliers[i].margin;

    if(this.state.row.margin != null || this.state.row.margin != undefined) var marginProduct = (this.state.row.margin/100)+1;
    else var marginProduct = (margValSupplier/100)+1;

    var totalTax = pvp-(pvp/taxValue);
    var margTotalBob = (pvp/taxValue)-((pvp/taxValue)/marginProduct);
    var costBob = pvp-(margTotalBob+totalTax);

    if(this.state.row.dollar == 0 || this.state.row.dollar == undefined) var totalDollar = 0;
    else var totalDollar = this.state.row.pvp/this.state.row.dollar;

    this.setState({
      totalTax: Math.round(totalTax, -1),
      margTotalBob: Math.round(margTotalBob, -1),
      costBob: Math.round(costBob, -1),
      margValSupplier:margValSupplier,
      totalDollar:Math.round(totalDollar,-1)
    })
  }

  status(cell){
    if(this.state.row.status){
        return(
            <AppSwitch id="status" onChange={this.changeInputs.bind(this,"status")} className={'mx-1'} variant={'pill'} color={'success'} label checked/>
        );
      }else{
        return(
           <AppSwitch id="status" onChange={this.changeInputs.bind(this,"status")} className={'mx-1'} variant={'pill'} color={'success'} label  />
        );
      }
  } 

  description(value) {
    var rowTmp = this.state.row;
    rowTmp.description = value;
    this.setDate(rowTmp);
  }

  sdescription(value) {
      var rowTmp = this.state.row;
      rowTmp.sdescription = value;
      this.setDate(rowTmp);
  }

  clickCategories(id, name){
    var rowTmp = this.state.row;
    if(document.getElementById("cat_"+id).checked){
      rowTmp.categories.push({id:id, name:name});
    }
    else{
      var TempCate = [];
      rowTmp.categories.forEach((val) => {
        if(val.id != id) TempCate.push(val);
      })
      rowTmp.categories = TempCate;
    }
    this.setDate(rowTmp);
  }

  categories(){
   const listCategories = JSON.parse(localStorage.getItem("listCategories"));
   const cateObj = this.state.row.categories;
   var obj = [];
   var flag = false;
   var key = "";
   try{
    listCategories.forEach(element => {
      cateObj.forEach((val) => { if(element._id == val.id)flag = true;})
      key = "catChec"+element._id;
      obj.push(
        <Col key={key} xs="3" sm="3" md="3">
          <FormGroup  check className="checkbox">
            <Input className="form-check-input" type="checkbox" id={"cat_"+element._id} name={element.name} value={element._id} onClick={this.clickCategories.bind(this,element._id,element.name)} defaultChecked={flag}/>
            <Label check className="form-check-label">
              {element.name}
            </Label>
          </FormGroup>
        </Col>
      );

      flag = false;
    });
   }catch(e){}
    return obj;
  }

  saveLocalStorage(rowTmp){
    localStorage.setItem('row', JSON.stringify(rowTmp));
  }

  setDate(value,file){
    var tabAct = localStorage.getItem("activeTab");
    this.setState({activeTab:(tabAct)?tabAct:'1', row:value});
    this.saveLocalStorage(value);
    if(file == "tax" || file == "margin" || file == "pvp" || file == "dollar" ) this.calculatePrices();
  }

  changeInputs(idInput){
    var rowTmp = this.state.row;
    var value = document.getElementById(idInput).value;
    if(idInput == "code") rowTmp.code = value;
    if(idInput == "reference") rowTmp.reference = value;
    if(idInput == "name") rowTmp.name = value;
    if(idInput == "supplier") rowTmp.idSupplier = value;
    if(idInput == "brand") rowTmp.idBrand = value;
    if(idInput == "status") rowTmp.status = document.getElementById(idInput).checked;
    if(idInput == "tax") { rowTmp.idTax = value; }
    if(idInput == "dollar") rowTmp.dollar = value;
    if(idInput == "stock") rowTmp.stock = value;
    if(idInput == "margin"){
      if(value < this.state.margValSupplier) value = this.state.margValSupplier;
      rowTmp.margin = value;
    }
    if(idInput == "pvp")rowTmp.pvp = value;
    this.setDate(rowTmp,idInput);
  }

  selects(name, objSelect, id){
    var listObj = JSON.parse(localStorage.getItem(objSelect));
    var opTmp = [];
    var key = "";
    listObj.forEach((val) => {
      key = name+"_"+val._id;
      if(id == val._id) 
        opTmp.unshift(<option key={key} value={val._id}>{val.name}</option>)
      else
        opTmp.push(<option key={key} value={val._id}>{val.name}</option>)
     });

    return (
      <Input type="select" id={name} onChange={this.changeInputs.bind(this,name)} name={name} key={name}>
        {opTmp}
      </Input>
    );
  }

  selectRolSupplier(){
    if(this.props.userdate.rol == 5){
      return this.selects("supplier","listSuppliers", this.state.row.idSupplier);
    }else{
      var listSuppliers = JSON.parse(localStorage.getItem("listSuppliers"));
      for(var i=0; i<listSuppliers.length; i++) if(listSuppliers[i]._id == this.props.userdate.idSupplier) break;
      return <Input type="text" id="supplier" required defaultValue={listSuppliers[i].name} disabled/>
    }
  }

  setStores(){
    var arrayStores = [];
    var listStores = JSON.parse(localStorage.getItem("listStores"));
    arrayStores.push({value:"all", label:"Todas", disable:true});
    listStores.forEach(val => {
      arrayStores.push({value:val._id,label:val.name,disable:true});
    });

    var discount = JSON.parse(localStorage.getItem("discount"));
    if(discount == null){
      var selectStores = ["all"]
    }else{
      var selectStores = discount.stores;
      if(selectStores == null){ selectStores = ["all"]}
    }
    this.setState({arrayStores, selectStores});
  }

  addStore(val){
    var selectStores = [];
    if(val.length == 0) selectStores = ["all"];
    val.forEach(val =>{
      if(val.value == "all" && val.length == 1)selectStores.push(val);
      if(val.value != "all") selectStores.push(val);
    })
    var discount = JSON.parse(localStorage.getItem("discount"));
    if(discount == null) localStorage.setItem("discount",JSON.stringify({stores:selectStores}));
    else{
      discount.stores = selectStores;
      localStorage.setItem("discount",JSON.stringify(discount));
    } 

    this.setState({selectStores});
  }

  setDate(){
    var discount = JSON.parse(localStorage.getItem("discount"));
    if(discount != null && discount.dates != undefined){
      console.log(JSON.parse(localStorage.getItem("discount")).dates.startDate);
      //this.setDate({
      //  startDate: null,
      //  endDate: null
      //})
    }
  }

  addDates({startDate, endDate}) {
    this.setState({ startDate, endDate });
    startDate = moment(startDate).format('YYYY/MM/DD');
    endDate = moment(endDate).format('YYYY/MM/DD');
    var discount = JSON.parse(localStorage.getItem("discount"));
    if(discount == null) localStorage.setItem("discount",JSON.stringify({dates:{startDate,endDate}}));
    else{
      discount.dates = {dates:{startDate,endDate}}
      localStorage.setItem("discount",JSON.stringify(discount));
    }
  }

  onFocusChange(focusedInput) {this.setState({ focusedInput });}

  render() {

    const customStyles = {width: "180%"}

    return (
      <Row>
        <Col xs="12" md="12">
            <Row>
                <Col xs="12" md="12">
                    <Card>
                        <CardHeader>
                            Editar o Crear un nuevo producto
                        </CardHeader>
                        <CardBody>
                            <Nav tabs>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '1' })} onClick={() => { this.toggle('1'); }}>
                                  <i className="icon-note"></i> <span> Descripcin</span>
                                </NavLink>
                              </NavItem>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '2' })}  onClick={() => { this.toggle('2'); }} >
                                  <i className="icon-wallet"></i> <span> Precios</span>
                                </NavLink>
                              </NavItem>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '3' })} onClick={() => { this.toggle('3'); }} >
                                  <i className="icon-picture"></i> <span> Imagenes y Videos</span>
                                </NavLink>
                              </NavItem>
                              <NavItem>
                                <NavLink className={classnames({ active: this.state.activeTab === '4' })} onClick={() => { this.toggle('4'); }} >
                                  <i className="icon-tag"></i> <span> Descuentos</span>
                                </NavLink>
                              </NavItem>
                            </Nav>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="1">
                                <Row>
                                  <Col>
                                    <Label>Codigo (EAN, SKU, UPC): </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-barcode"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="code" placeholder="Ingrese el cdigo" required defaultValue={this.state.row.code} onChange={this.changeInputs.bind(this,"code")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Referencia</Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="reference" placeholder="Ingrese la referencia" defaultValue={this.state.row.reference} onChange={this.changeInputs.bind(this,"reference")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col className="pb-4">
                                    <Row>
                                      <Col xs="3" sm="3" md="3">
                                        <Label>Estado: </Label>
                                        <InputGroup>
                                          {this.status()}
                                        </InputGroup>
                                      </Col>
                                      <Col xs="9" sm="9" md="9">
                                        <Label>Inventario:</Label>
                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-map-pin"></i></InputGroupText>
                                          </InputGroupAddon>
                                          <Input type="text" id="stock" placeholder="Ingrese el nmero de unidades" defaultValue={this.state.row.stock} onChange={this.changeInputs.bind(this,"stock")}/>
                                        </InputGroup>
                                      </Col>
                                    </Row>
                                  </Col>
                                </Row>
                                <Row className="pb-3">
                                  <Col>
                                    <Label>Nombre: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-edit"></i></InputGroupText>
                                      </InputGroupAddon>
                                      <Input type="text" id="name" placeholder="Ingrese el cdigo" required defaultValue={this.state.row.name} onChange={this.changeInputs.bind(this,"name")}/>
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Marca: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-cubes"></i></InputGroupText>
                                      </InputGroupAddon>
                                      {this.selects("brand","listBrands", this.state.row.idBrand)}
                                    </InputGroup>
                                  </Col>
                                  <Col>
                                    <Label>Vendedor: </Label>
                                    <InputGroup>
                                      <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i className="fa fa-reddit-alien"></i></InputGroupText>
                                      </InputGroupAddon>
                                      {this.selectRolSupplier()}
                                    </InputGroup>
                                  </Col>
                                </Row>
                                <Row>
                                  <Col>
                                    <Label>Categorias: </Label>
                                    <Card>
                                      <Row style={{padding:"10px"}}>
                                        {this.categories()}
                                      </Row>
                                    </Card>
                                  </Col>
                                  {/*<Col>Caracteristicas</Col>
                                  <Col>SEO</Col>*/}
                                </Row>
                                <Row>
                                  <Col>
                                    <Label>Descripcin Corta:</Label>
                                    <ReactQuill value={this.state.row.sdescription} modules={this.editor}  onChange={this.sdescription}/>
                                  </Col>
                                  <Col>
                                    <Label>Descripcin Larga:</Label>
                                    <ReactQuill value={this.state.row.description} modules={this.editor} onChange={this.description} />
                                  </Col>
                                </Row>
                              </TabPane>
                            </TabContent>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="2">
                                <Row className="pb-3">
                                  <Col xs="8" sm="8" md="8">
                                    <Row>
                                      <Col xs="3" sm="3" md="3">
                                        <Label>Precio de venta (PVP): </Label>
                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-money"></i></InputGroupText>
                                          </InputGroupAddon>
                                          <Input type="number" id="pvp" placeholder="Ingrese el costo" required defaultValue={this.state.row.pvp} onChange={this.changeInputs.bind(this,"pvp")}/>
                                        </InputGroup>
                                      </Col>
                                      <Col xs="3" sm="3" md="3">
                                        <Label>Impuesto: </Label>
                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-institution"></i></InputGroupText>
                                          </InputGroupAddon>
                                          {this.selects("tax","listTaxes", this.state.row.idTax)}
                                        </InputGroup>
                                      </Col>
                                      <Col xs="3" sm="3" md="3">
                                      <Label>Margen (Minimo {this.state.margValSupplier})</Label>
                                      <InputGroup>
                                        <InputGroupAddon addonType="prepend">
                                          <InputGroupText><i className="fa fa-line-chart"></i></InputGroupText>
                                        </InputGroupAddon>
                                        <Input type="Number" id="margin" placeholder="Ingrese el margen" defaultValue={this.state.row.margin} onChange={this.changeInputs.bind(this,"margin")}/>
                                      </InputGroup>
                                    </Col>
                                      <Col xs="3" sm="3" md="3" className="pb-2">
                                        <Label>Dolar Referencia:</Label>
                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-dollar"></i></InputGroupText>
                                          </InputGroupAddon>
                                          <Input type="text" id="dollar" placeholder="Ingrese la referencia" defaultValue={this.state.row.dollar} onChange={this.changeInputs.bind(this,"dollar")}/>
                                        </InputGroup>
                                      </Col>

                                      <Col xs="12" sm="12" md="12" className="pb-2">
                                        <Label>Aplicar descuento a tiendas: </Label>
                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-shopping-bag"></i></InputGroupText>
                                          </InputGroupAddon>
                                          <Select 
                                              name="stores"
                                              value={this.state.selectStores}
                                              options={this.state.arrayStores}
                                              onChange={this.addStore}
                                              multi
                                          />
                                        </InputGroup>
                                      </Col>
                                      <Col xs="4" sm="4" md="4">
                                        <Label>Fecha Promocin: </Label>
                                        <Card>
                                          <InputGroup>
                                            <InputGroupAddon addonType="prepend">
                                              <InputGroupText><i className="fa fa-calendar"></i></InputGroupText>
                                            </InputGroupAddon>
                                            <DateRangePicker 
                                              startDate={startDate = () => {return "2020-09-20"} }
                                              endDate={this.state.endDate}
                                              startDateId="startDate"
                                              endDateId="endDate"
                                              onDatesChange={this.addDates}
                                              focusedInput={this.state.focusedInput}
                                              onFocusChange={this.onFocusChange}
                                              orientation={this.state.orientation}
                                              openDirection={this.state.openDirection}
                                              startDatePlaceholderText="Fecha Inicial"
                                              endDatePlaceholderText="Fecha Final"
                                              small={true}
                                              regular={true}
                                              block={true}
                                              noBorder={true}
                                            />
                                          </InputGroup>
                                        </Card>
                                      </Col>
                                      <Col xs="4" sm="4" md="4">
                                        <Label>Descuento: </Label>
                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-money"></i></InputGroupText>
                                          </InputGroupAddon>
                                          <Input type="number" id="valueD" placeholder="Ingrese el descuento" required defaultValue={this.state.row.discount.value} onChange={this.changeInputs.bind(this,"valueD")}/>
                                        </InputGroup>
                                      </Col>
                                      <Col xs="4" sm="4" md="4">
                                        <Label>&nbsp;</Label>
                                        <Button active block color="success" aria-pressed="true">+ Guardar Descuento</Button>
                                      </Col>
                                    </Row>
                                  </Col>
                                  <Col xs="4" sm="4" md="4">
                                    <Card className="p-2" style={{border:"2px solid #444d58", backgroundColor:"#eff3f8"}}>
                                        <Col> 
                                          <Row>
                                            <Col style={{fontWeight:"bold"}}>Costo Bob:</Col>
                                            <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.costBob} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                          </Row>
                                        </Col>
                                        <Col> 
                                          <Row>
                                            <Col style={{fontWeight:"bold"}}>Margen Bob:</Col>
                                            <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.margTotalBob} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                          </Row>
                                        </Col>
                                        <Col> 
                                          <Row>
                                            <Col style={{fontWeight:"bold"}}>Impuestos:</Col>
                                            <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.totalTax} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                          </Row>
                                        </Col>
                                        <Col>
                                          <Row>
                                            <Col style={{fontWeight:"bold"}}>Total (PVP):</Col>
                                            <Col style={{textAlign:"right", fontWeight:"bold"}}><CurrencyFormat value={this.state.row.pvp} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                          </Row> 
                                        </Col>
                                        <Col>
                                          <Row>
                                            <Col style={{fontWeight:"bold"}}>Dolar ref:</Col>
                                            <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.totalDollar} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                          </Row> 
                                        </Col>
                                      </Card>
                                  </Col>  
                                </Row>

                              </TabPane>
                            </TabContent>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="3">nada3</TabPane>
                            </TabContent>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="4">nada4</TabPane>
                            </TabContent>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        </Col>
      </Row>

    );
  }
}

