import React, { Component } from 'react';
import { Pagination, PaginationItem, PaginationLink, Input } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDoubleLeft, faAngleLeft, faAngleRight, faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons';


class ObjPag extends Component {

    constructor(props) {
        super(props);
        this.registerChange = this.registerChange.bind();
    }

    pagRender(e,value){
        const RegPerFind = this.props.info.RegPerFind;
        const NumPages = this.props.info.NumPages;
        const LimtPage = this.props.info.LimtPage;
        const NowPage = this.props.info.NowPage;
        var rendPage = 5;
        console.log(value);
        rendPage = (NumPages < 10)? NumPages : 10;
        const pag = [];

        for (let i=1; i<=rendPage; i++) {
            if(i == NowPage){
                pag.push(
                    <PaginationItem key={"pag"+i} active>
                        <PaginationLink onClick={this.props.pagLoad.bind(this,""+i)}>
                            {i}
                        </PaginationLink>
                    </PaginationItem>
                )
            }else{
                pag.push(
                    <PaginationItem key={"pag"+i}>
                        <PaginationLink onClick={this.props.pagLoad.bind(this,""+i)}>
                            {i}
                        </PaginationLink>
                    </PaginationItem>
                )
            }
        }
        return pag;
    }

   // registerChanged(e){
     //   console.log(e.target.value)
        //this.props.pagLoad.bind(this, "changeRegister", (event) => {return event} )
    //  }

      registerChange(e){
        console.log(`Option selected:`, e.target.value );
        this.props.pagLoad.bind(this, "changeRegister", e.target.value )
    }

    render(){
        return (
            <Pagination className="pull-right" >
                <Input type="select" name="select" id="pagNum" onChange={this.registerChange} >
                    <option value="10">10</option>
                    <option value="20">20</option>
                    <option value="50">50</option>
                    <option value="100">100</option>
                    <option value="1000">1000</option>
                </Input>
                <PaginationItem key="allback">
                    <PaginationLink onClick={this.props.pagLoad.bind(this, 'allback')}>
                        <FontAwesomeIcon  icon={faAngleDoubleLeft} />
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem key="back">
                    <PaginationLink previous onClick={this.props.pagLoad.bind(this, "back")}>
                        <FontAwesomeIcon  icon={faAngleLeft} />
                    </PaginationLink>
                </PaginationItem>

                {this.pagRender()}

                <PaginationItem key="forward">
                    <PaginationLink onClick={this.props.pagLoad.bind(this, "forward")}>
                        <FontAwesomeIcon  icon={faAngleRight} />
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem key="allforward">
                    <PaginationLink previous onClick={this.props.pagLoad.bind(this, "allforward")}>
                        <FontAwesomeIcon  icon={faAngleDoubleRight} />
                    </PaginationLink>
                </PaginationItem>
            </Pagination>
        );
    }
}

export default ObjPag;
