import { connect } from 'react-redux';
import React, { Component } from 'react';
import {
  Badge, Button, ButtonDropdown, Card, CardBody, CardFooter,
  CardHeader, Col, Collapse, DropdownItem, DropdownMenu,
  DropdownToggle, Fade, Form, FormGroup, FormText, FormFeedback,
  Input, InputGroup, InputGroupAddon, InputGroupText, Label, Row,
} from 'reactstrap';

class MyAccount extends Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.toggleFade = this.toggleFade.bind(this);
    this.state = {
      collapse: true,
      fadeIn: true,
      timeout: 300
    };
  }

  componentDidUpdate(){
    // console.log("emailll  "+this.props.email);
    this.props.dispatch('userFind',this.props.email);
  }

  toggle() {
    this.setState({ collapse: !this.state.collapse });
  }

  toggleFade() {
    this.setState((prevState) => { return { fadeIn: !prevState }});
  }

  render() {
    return (
      <div className="animated fadeIn">

        <Row>
          <Col xs="12" sm="6">
            <Card>
              <CardHeader>
                <strong>Compañia</strong>
              </CardHeader>
              <CardBody>
                <FormGroup>
                  <Label htmlFor="email">Correo</Label>
                  <Input type="text" id="company" placeholder="Email" value={this.props.email} disabled/>
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="company">Nombre Empresa</Label>
                  <Input type="text" id="company" placeholder="Nombre Fiscal de Empresa" />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="tradeName">Nombre Empresa</Label>
                  <Input type="text" id="tradeName" placeholder="Nombre Comercial" />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="nit">NIT</Label>
                  <Input type="text" id="nit" placeholder="98765233-2" />
                </FormGroup>
              </CardBody>
            </Card>
          </Col>
          <Col xs="12" sm="6">
          <Fade timeout={this.state.timeout} in={this.state.fadeIn}>
            <Card>
            <CardHeader>
                  <i className="fa fa-edit"></i>Dirección
                  <div className="card-header-actions">
                    <Button color="link" className="card-header-action btn-minimize" data-target="#address1" onClick={this.toggle}><i className="icon-arrow-up"></i></Button>
                  </div>
            </CardHeader>
            <Collapse isOpen={this.state.collapse} id="address1">
            <CardBody>
                <FormGroup>
                  <Label htmlFor="street">Street</Label>
                  <Input type="text" id="street" placeholder="Enter street name" />
                </FormGroup>
                <FormGroup row className="my-0">
                  <Col xs="8">
                    <FormGroup>
                      <Label htmlFor="city">City</Label>
                      <Input type="text" id="city" placeholder="Enter your city" />
                    </FormGroup>
                  </Col>
                  <Col xs="4">
                    <FormGroup>
                      <Label htmlFor="postal-code">Postal Code</Label>
                      <Input type="text" id="postal-code" placeholder="Postal Code" />
                    </FormGroup>
                  </Col>
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="country">Country</Label>
                  <Input type="text" id="country" placeholder="Country name" />
                </FormGroup>
              </CardBody>
            </Collapse>
            </Card>
          </Fade>
        </Col>

        </Row>

      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return{
    email: state.login.email,
    password: state.login.password
  }
}

const mapDispatchToProps = (dispatch) => {
  return{
    dispatch: (type,email) => {
      dispatch({type:type,email})  
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
