                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-shopping-bag"></i></InputGroupText>
                                          </InputGroupAddon>
                                          <Select 
                                              name="stores"
                                              value={this.state.selectStores}
                                              options={this.state.arrayStores}
                                              onChange={this.addStore}
                                              multi
                                          />
                                        </InputGroup>
                                      </Col>
                                      <Col xs="4" sm="4" md="4">
                                        <Label>Fecha Promoción: </Label>
                                        <Card>
                                          <InputGroup id="datePicker">
                                            <InputGroupAddon addonType="prepend">
                                              <InputGroupText><i className="fa fa-calendar"></i></InputGroupText>
                                            </InputGroupAddon>
                                            <DateRangePicker
                                              startDate={this.state.startDate}
                                              endDate={this.state.endDate}
                                              startDateId="startDate"
                                              endDateId="endDate"
                                              onDatesChange={this.addDatePicker}
                                              focusedInput={this.state.focusedInput}
                                              onFocusChange={this.onFocusChange}
                                              orientation={this.state.orientation}
                                              openDirection={this.state.openDirection}
                                              startDatePlaceholderText="Fecha Inicial"
                                              endDatePlaceholderText="Fecha Final"
                                              small={true}
                                              regular={true}
                                              block={true}
                                              noBorder={true}
                                            />
                                          </InputGroup>
                                        </Card>
                                      </Col>
                                      <Col xs="4" sm="4" md="4">
                                        <Label>Descuento: </Label>
                                        <InputGroup>
                                          <InputGroupAddon addonType="prepend">
                                            <InputGroupText><i className="fa fa-money"></i></InputGroupText>
                                          </InputGroupAddon>
                                          <Input type="number" key="valueD" id="valueD" placeholder="Ingrese el costo" required defaultValue={this.setDiscountVal()} onChange={this.addDiscountVal.bind(this)}/>
                                        </InputGroup>
                                      </Col>
                                      <Col xs="4" sm="4" md="4">
                                        <Label>&nbsp;</Label>
                                        <Button active block color="success" aria-pressed="true" onClick={this.addDiscountTable.bind(this)}>+ Guardar Descuento</Button>
                                      </Col>
                                      <Col xs="12" sm="12" md="12">
                                        <BootstrapTable  data={this.state.discountTable} striped hover options={this.options}  > 
                                          <TableHeaderColumn width={'35%'} dataFormat={this.editTableStores.bind(this)} >Tiendas</TableHeaderColumn>
                                          <TableHeaderColumn width={'35%'} dataFormat={this.editTableDates.bind(this)} >Fechas</TableHeaderColumn>
                                          <TableHeaderColumn width={'20%'} dataFormat={this.editTableValue.bind(this)} dataAlign="right">Valor</TableHeaderColumn>
                                          <TableHeaderColumn width={'10%'} dataFormat={this.editFormatter.bind(this)} >Accion</TableHeaderColumn>
                                          <TableHeaderColumn isKey={true} dataField='id' hidden>Identifier ID</TableHeaderColumn>
                                        </BootstrapTable>
                                      </Col>
                                    </Row>
                                  </Col>
                                  <Col xs="4" sm="4" md="4">
                                    <Card className="p-2" style={{border:"2px solid #444d58", backgroundColor:"#eff3f8"}}>
                                      <Col>
                                        <Row>
                                          <Col xs="4" sm="4" md="4" style={{fontWeight:"bold"}}>Fecha:</Col>
                                          <Col xs="8" sm="8" md="8" style={{textAlign:"right", fontWeight:"bold", color:"green"}}>{this.state.startDateD} -> {this.state.endDateD}</Col>
                                        </Row>
                                        <Row>
                                          <Col style={{fontWeight:"bold"}}>% Descuento:</Col>
                                          <Col style={{textAlign:"right", fontWeight:"bold", color:"red"}}> {this.state.reduction} %</Col>
                                        </Row>
                                        <Row>
                                          <Col style={{fontWeight:"bold"}}>Costo Bob:</Col>
                                          <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.costBobD} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                        </Row>
                                      </Col>
                                      <Col> 
                                        <Row>
                                          <Col style={{fontWeight:"bold"}}>Margen Bob:</Col>
                                          <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.margTotalBobD} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                        </Row>
                                      </Col>
                                      <Col> 
                                        <Row>
                                          <Col style={{fontWeight:"bold"}}>Impuestos:</Col>
                                          <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.totalTaxD} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                        </Row>
                                      </Col>
                                      <Col>
                                        <Row>
                                          <Col style={{fontWeight:"bold"}}>Total (PVP):</Col>
                                          <Col style={{textAlign:"right", fontWeight:"bold"}}><CurrencyFormat value={this.state.pvpD} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                        </Row> 
                                      </Col>
                                      <Col>
                                        <Row>
                                          <Col style={{fontWeight:"bold"}}>Dolar ref:</Col>
                                          <Col style={{textAlign:"right"}}><CurrencyFormat value={this.state.totalDollarD} displayType={'text'} thousandSeparator={true} decimalSeparator={"."}  prefix={'$'} renderText={value => value} /></Col>
                                        </Row> 
                                      </Col>
                                    </Card>
                                  </Col>
                                </Row>

                              </TabPane>
                            </TabContent>
                            <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="3">
                                <CloudinaryContext cloudName={this.props.userdate.cloudName} uploadPreset={this.props.userdate.uploadPreset}>
                                  <Button onClick={this.beginUpload.bind(this)} size="sm" className="btn btn-success active">
                                    + Cargar Imagen
                                  </Button>
                                  <br/><br/>
                                  <Card>
                                    <Col>
                                      <Row>
                                        { this.state.row.images.map(i => 
                                          <Col key={"img"+i.url} xs="2" sm="2" md="2">
                                            <Image publicId={i.url} >
                                              <Transformation width="208" crop="scale" />
                                            </Image>
                                            <Button style={{position:"absolute", marginTop:"-30px"}} onClick={this.removeImage.bind(this, i.url)} size="sm" className="btn btn-danger active">
                                              Eliminar
                                            </Button>
                                            <Button size="sm" style={{position:"inherit", marginTop:"-30px"}} onClick={this.topImage.bind(this, i.url)} className="float-right btn btn-success active">
                                              Principal
                                            </Button>
                                          </Col>
                                        )}
                                      </Row>
                                    </Col>
                                  </Card>
                                </CloudinaryContext>
                              </TabPane>
                            </TabContent>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        </Col>
      </Row>

    );
  }
}

const mapStateToProps = (state) => {
  return{
    obj:state.DaoProducts
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body, method) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body, method);
    }

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Eidts);
