import { connect } from 'react-redux';
import React, { Component } from 'react';
import { CSVLink } from "react-csv";
import { Row, Form, FormGroup, NavLink,Input, CardHeader, Col, CardBody, Card } from 'reactstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import CurrencyFormat from 'react-currency-format';
import DbCrud from '../../../Pojo/DbCrud';


class Edit extends Component {

  constructor(props) {
    super(props);
    this.state = {
      orders: {
        pag:"1",
        limit:"20",
        filFile:"order",
        filValue: ""+this.props.match.params.Order,
        sorts:[
          {
            file:"oderDate",
            value:1
          }
        ]
      }
    }

  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    this.props.loadReducer('loadOrdersEdit', "orders/find/edit",this.state.orders);
  }

  callList() { 
    this.props.history.push('/Orders/List'); 
  }

  callSave(e) {
    e.preventDefault();
    //this.props.update('updateGeo', "geo", "CountriesList", this, this.state.documentId);
    this.props.history.push('/Orders/List');
  }

  onFormSubmit = (e) => {
    e.preventDefault();
    const target = e.target;
    var tmpfFile = "";
    var tmpfValue = "";

    //Se selecciona de atras para delante un solo filtro 
    if(target.customer.value != ""){
      tmpfFile = "customer";
      tmpfValue = target.customer.value+"";
    }else{
      if(target.dni.value != ""){
        tmpfFile = "dni";
        tmpfValue = target.dni.value+"";
      }else{
        if(target.order.value != ""){
          tmpfFile = "order";
          tmpfValue = target.order.value+"";
        }else{
          if(target.supplier.value != "0"){
            tmpfFile = "supplier";
            tmpfValue = target.supplier.value+"";
          }else{
            if(target.brand.value != "0"){
              tmpfFile = "brand";
              tmpfValue = e.target.brand.value+"";
            }else{
              if(target.store.value != "0"){
                tmpfFile = "store";
                tmpfValue = e.target.store.value+"";
              }else{
                if(target.status.value != "0"){
                  tmpfFile = "status";
                  tmpfValue = e.target.status.value+"";
                  console.log(tmpfValue);
                }
              }
            }
          }
        }
      }
    }

    if(tmpfValue == "0") tmpfValue = "";

    //Se realiza la solicitud nuevamente para recargar los registros 
    this.props.loadReducer('loadOrders', "orders/find", {
      pag:1,
      limit:5,
      filFile:tmpfFile,
      filValue:tmpfValue,
      sorts:this.state.orders.sorts
    });

    //Actualizamos el esta con los valores de filtros seleccionados esto por si se utiliza la 
    //paginacion por lo que se requiere que el estado mantenga el filtro de campo 
    this.setState({
        orders:{
          pag:1,
          limit:5,
          filFile:tmpfFile,
          filValue:tmpfValue,
          sorts:this.state.orders.sorts
        }
      }
    );
  }

  selectStatus(){
    const formOrders = [];
    const stausNodes = this.props.obj.listOrdersEditStatus; 
    console.log(stausNodes);
    Object.keys(stausNodes).forEach(function(key){
      var keys = "status"+key;
      formOrders.push(<option key={keys} value="0">stausNodes[key]</option>);
    })
    return formOrders;
  }

  formOrders(){
    const nodes = this.props.obj.listOrdersEdit;
    const formOrders = [];
    const onSubmit = this.onFormSubmit.bind();
    const reference = this.props.match.params.Reference;
    const selectStatus = this.selectStatus.bind(this);
    var flag = true;
    Object.keys(nodes).forEach(function(key){
      var keyname = "order"+key;
      var color = (reference == nodes[key].reference)? "#a9a9a347":"";
      if(flag){
        flag = false;
        formOrders.push(
          <CardBody key="headOrder">
            <Row>
              <Col xs="4" md="4"><strong>Orden:</strong> {nodes[key].order}</Col>
              <Col xs="4" md="4"><strong>Fecha:</strong> {(nodes[key].date).substr(0,10)}</Col>
              <Col xs="4" md="4"><strong>Departamento:</strong> {nodes[key].state}</Col>

              <Col xs="4" md="4"><strong>Cliente:</strong> {nodes[key].customer}</Col>
              <Col xs="4" md="4"><strong>Celular:</strong> {nodes[key].phone}</Col>
              <Col xs="4" md="4"><strong>Ciudad:</strong> {nodes[key].city}</Col>

              <Col xs="4" md="4"><strong>DNI:</strong> {nodes[key].dni}</Col>
              <Col xs="8" md="8"><strong>Dirección:</strong> {nodes[key].address} {nodes[key].addAddress} - {nodes[key].neighborhood}</Col>
            </Row>
          </CardBody>
        )

      }

      formOrders.push(
        <div key={keyname}>
          <CardBody style={{
            borderBottom:"1px solid rgb(193, 193, 193)",
            borderTop:"1px solid rgb(193, 193, 193)", 
            padding:"10px 10px 10px 10px",
            backgroundColor:color
            }}>
            <Row>
              <Col xs="2" md="2">
                <Form action="" onSubmit={onSubmit} method="post" className="form-horizontal">
                  <FormGroup>
                    <Row>
                      <Col>
                        <Input type="select" name={nodes[key].reference} id={nodes[key].reference} key={nodes[key].reference}>
                          {selectStatus()}
                        </Input>
                      </Col>
                    </Row>
                  </FormGroup>
                </Form>
              </Col>
              <Col xs="1" md="1" style={{textAlign:"center"}}>
                <img src="https://solutionssap.com/wp-content/uploads/2016/05/Dummy.png" alt="Img Prod" style={{maxWidth:"60px", maxHeight:"60px"}} />
              </Col>
              <Col xs="9" md="9">
                <Row>
                  <Col xs="4" md="4">
                    <Row><strong>{nodes[key].skuName}</strong></Row>
                    <Row><strong>Ref:</strong>{nodes[key].reference}&nbsp;-&nbsp;<strong>Cod:</strong>{nodes[key].code}</Row>
                  </Col>
                  <Col xs="2" md="2">
                    <Row><strong>Cantidad:</strong>({nodes[key].lot})</Row>
                    <Row>
                      <strong>Valor:</strong>&nbsp; 
                      <CurrencyFormat value={(nodes[key].value != null)?nodes[key].value:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Descuento:</strong>&nbsp;
                      <CurrencyFormat value={(nodes[key].discount != null)?nodes[key].discount:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                  </Col>
                  <Col xs="2" md="2">
                    <Row>
                      <strong>Puntos:</strong>&nbsp; 
                      <CurrencyFormat value={(nodes[key].points != null)?nodes[key].points:0} displayType={'text'} thousandSeparator={true} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Precio:</strong>&nbsp; 
                      <CurrencyFormat value={(nodes[key].price != null)?nodes[key].price:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Envio:</strong>&nbsp;
                      <CurrencyFormat value={(nodes[key].shipping != null)?nodes[key].shipping:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                  </Col>
                  <Col xs="2" md="2">
                    <Row>
                      <strong>Total:</strong>&nbsp; 
                      <CurrencyFormat value={(nodes[key].totalPay != null)?nodes[key].totalPay:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Trans:</strong>&nbsp;<a href={nodes[key].trakingUrl} target="_blank">{nodes[key].carrier}</a>
                    </Row>
                    <Row>
                      <strong>Guia:</strong>&nbsp;{nodes[key].guide}
                    </Row>
                  </Col >
                  <Col xs="2" md="2">
                    <Row><strong>Factura:</strong>&nbsp;<a href={nodes[key].invoiceUrl} target="_blank">{nodes[key].invoice}</a></Row>
                    <Row><strong>Marca:</strong>&nbsp;{nodes[key].brand}</Row>
                    <Row><strong>Tienda:</strong>&nbsp;{nodes[key].store}</Row>
                  </Col>
                </Row>
              </Col>
            </Row>
          </CardBody>
        </div>
      )

    })

    return formOrders;

  }

  render() {

    return (
      <div className="animated fadeIn">
        <Card>
          <CardHeader>
            Programación de Ordenes
            <div className="card-header-actions">
              <CSVLink data={this.props.obj.listOrdersEdit}>Descargar Busqueda</CSVLink>
            </div>
          </CardHeader>
          {this.formOrders()}
        </Card>
      </div>
    );
  }

}

