import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { openUploadWidget } from './CloudinaryService';
import { photosUploaded } from './actions';
import Photo from './Photo';
import {CloudinaryContext} from 'cloudinary-react';

class PhotoList extends Component {
    render() {
        return (
            <div className="photoList">
                <div className="actions">
                    <a className="upload_link" onClick={this.uploadImageWithCloudinary.bind(this)} >
                        Add photos with Cloudinary File upload
                    </a>
                </div>
                <div className="photos">
                    {this.props.photos.length === 0 && (
                        <p>No photos were added yet.</p>
                    )}
                    {this.props.photos.map(photo => {
                        return (
                            <Photo key={photo.public_id} publicId={photo.public_id} />
                        );
                    })}
                </div>
                <div className="note">
                    Take a look at our documentation of{' '}
                    <a href="https://cloudinary.com/documentation/image_transformations" target="_blank" rel="noopener noreferrer" >
                        Image Transformations
                    </a>{' '}
                    for a full list of supported transformations.
                </div>
            </div>
        );
    }

    uploadImageWithCloudinary() {
        const uploadOptions = { tags: 'myphotoalbum', ...this.context };
        openUploadWidget(uploadOptions, (error, photos) => {
            if (!error) {
                this.props.onPhotosUploaded(photos);
            } else {
                console.log(error);
            }
        });
    }

    static contextType = CloudinaryContext.contextType;
}

PhotoList.propTypes = {
    photos: PropTypes.array,
    onPhotosUploaded: PropTypes.func,
};

const PhotoListContainer = connect(
    state => ({ photos: state.photos }),
    {
        onPhotosUploaded: photosUploaded,
    }
)(PhotoList);

Object.assign(PhotoListContainer.contextTypes, PhotoList.contextTypes);

export default PhotoListContainer;
