import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Row, Label, InputGroupAddon, InputGroup, InputGroupText, Col, Button, Input, Card, CardHeader, CardBody } from 'reactstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Actions/DbCrud';

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class Eidts extends Component {

  constructor(props) {
    super(props);
    this.state = {
        htmlCombinations : this.formCombinations.bind(this)
    }

    this.options = {
        sortIndicator: true,
        hideSizePerPage: true,
        paginationSize: 5,
        hidePageListOnlyOnePage: true,
        alwaysShowAllBtns: false,
        withFirstAndLast: false
      }
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    this.props.loadReducer("loadCombinations", "combinations/", {});
  } 

  option(){
      var options = [];
      options.push(<option key="comb0" value="0" disabled selected>Combinaciones</option>)
      try{
        this.props.obj.totalcombinations.label.forEach((elemt, item) => {
           var key = "comb"+(item+1);
           options.push(<option key={key} value={elemt}>{elemt}</option>)
        });
      }catch(e){}
      return options;
  }

  formCombinations(val){
    var html = [];
    return(
        <Row>
            <Col>
                <Row>
                    <Col>
                        <Label>Combinaci贸n: </Label>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                            <InputGroupText><i className="fa fa-calendar"></i></InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" id="name" placeholder="Ingrese el nombre de la Combinaci贸n" required />
                        </InputGroup>
                    </Col>
                </Row>
            </Col>
            <Col>
                Tabla
            </Col>
        </Row>
    )
  }

  render() {

    return (

      <Row>
        <Col xs="12" md="12">
            <Row>
                <Col xs="12" md="12">
                    <Card>
                        <CardHeader>
                            Seleccione la Combinaci贸n
                        </CardHeader>
                        <CardBody>
                            <Col xs="5" md="5">
                                <Row>
                                    <Col xs="12" md="12">
                                        <Row>
                                            <Col xs="12" md="8">
                                                <Input type="select" name="combinacion" id="combinacion" key="combinacion">
                                                    {this.option()}
                                                </Input>
                                            </Col>
                                            <Col xs="12" md="4"xl className="mb-2 mb-xl-0">
                                                <Button active block color="success" aria-pressed="true">+ Combinaci贸n</Button>
                                            </Col>
                                        </Row>
                                    </Col>
                                </Row>
                            </Col>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        </Col>

        <Col xs="12" md="12">
            <Row>
                <Col xs="6" md="6">
                    <Card>
                        <CardHeader>
                            Gestion de Combinaciones
                        </CardHeader>
                        <CardBody>
                            Formulario
                        </CardBody>
                    </Card>
                </Col>
                <Col xs="6" md="6">
                    <Card>
                        <CardHeader>
                            Atributos
                        </CardHeader>
                        <CardBody>
                            <BootstrapTable  data={this.props.obj.totalcombinations.Combinations} striped hover options={this.options} > 
                                <TableHeaderColumn width={'10%'} dataField='label' dataSort>Grupo</TableHeaderColumn>
                                <TableHeaderColumn width={'15%'} dataField='name' >Atributo</TableHeaderColumn>
                                <TableHeaderColumn dataFormat='' width={'10%'} dataAlign='right' >Accion</TableHeaderColumn>
                                <TableHeaderColumn isKey={true} dataField='_id' hidden>Identifier ID</TableHeaderColumn>
                            </BootstrapTable>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        </Col>
      </Row>

    );
  }
}

const mapStateToProps = (state) => {
  return{
    obj:state.DaoCombinations
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    }

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Eidts);
