import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, CardHeader, CardBody, Modal, ModalHeader, ModalBody, ModalFooter, Button, ButtonGroup } from 'reactstrap';
import { AppSwitch } from '@coreui/react'
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Pojo/DbCrud';

var flat = true;

class CountriesList extends Component {

  constructor(props) {
    super(props);
    this.state = {
      tmpRow : '',
      danger: false
    }
    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 3,
      hidePageListOnlyOnePage: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false
    }
    this.toggleDanger = this.toggleDanger.bind(this);
  }

  toggleDanger(row) {
    this.setState({
      danger: !this.state.danger,
      tmpRow: row.documentId
    });
  }

  componentDidMount() {
    if (flat) {
      flat = false;
      this.props.loadReducer('loadGeo', "geo");
    }
  }


  trashButton(row) {
    this.trashButton.bind(this, row);
    this.props.deleteReducer('deleteGeo', "geo", "CountriesList", row.documentId);
  }

  editFormatter(cell, row) {
    return (
      <ButtonGroup>
        <Button size="sm" className="btn-spotify btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
          <i className="icon-pencil"></i>
        </Button>
        <Button onClick={this.toggleDanger.bind(this, row)} size="sm" className="btn-reddit btn-brand icon mr-1 mb-1 btn btn-secondary btn-sm">
          <i className="icon-trash"></i>
        </Button>
      </ButtonGroup>
    )
  }

  editObject(row) {
    this.props.history.push('/Geo/StatesList/' + row.documentId);
  }

  loadStates(cell, row) {
    return (
      <Button size="sm" onClick={() => this.editObject(row)} className="btn-dropbox btn-brand text mr-1 mb-1 btn btn-secondary btn-lg">
        Ver Estados
      </Button>
    )
  }

  geopoint(cell, row) {
    return (
      <div>
        Lat: {row.geopoint.latitude} Lon:{row.geopoint.longitude}
      </div>
    );
  }

  active(cell, row) {
    if (row.active == '1') {
      return (
        <AppSwitch className={'mx-1'} variant={'pill'} color={'success'} label checked />
      );
    } else {
      return (
        <AppSwitch className={'mx-1'} variant={'pill'} color={'success'} label />
      );
    }
  }

  callCountryAdd() { this.props.history.push('/Geo/CountriesAdd'); }

  render() {

    return (
      <div className="animated">

        <Card>
          <CardHeader>
            Lista de Paises
            <div className="card-header-actions">
              <a href="https://github.com/AllenFang/react-bootstrap-table" rel="noopener noreferrer" target="_blank" className="card-header-action">
                <small className="text-muted">Aprende</small>
              </a>
            </div>
          </CardHeader>

          <Modal isOpen={this.state.danger} toggle={this.toggleDanger}
            className={'modal-danger ' + this.props.className}>
            <ModalHeader toggle={this.toggleDanger}>Modal title</ModalHeader>
            <ModalBody>Esta seguro que desea eliminar el Pais, al hacerlo se eliminaran todos los Estados y ciudades Asociados</ModalBody>
            <ModalFooter>
              <Button color="danger" onClick={this.trashButton.bind(this)}>Eliminar</Button>{' '}
              <Button color="secondary" onClick={this.toggleDanger}>Cancel</Button>
            </ModalFooter>
          </Modal>


          <CardBody>
            <Button color="primary" onClick={this.callCountryAdd.bind(this)} ><i className="fa icon-plus"></i>&nbsp;Crear Pais</Button>
            <BootstrapTable data={this.props.obj} striped hover pagination search options={this.options}>
              <TableHeaderColumn dataField='countryName' dataSort>Pais</TableHeaderColumn>
              <TableHeaderColumn dataField='isoCode' >Iso Code</TableHeaderColumn>
              <TableHeaderColumn isKey={true} dataField='documentId' hidden>Identifier ID</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.geopoint} >Geo Punto</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.active} dataAlign='center' >Activo</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.loadStates.bind(this)} dataAlign='right' >Estados</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.editFormatter.bind(this)} dataAlign='right' >Action</TableHeaderColumn>
            </BootstrapTable>
          </CardBody>
        </Card>
      </div>
    );
  }

}

const mapStateToProps = (state) => { return { obj: state.DaoGeo } }

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion) => { DbCrud.dbFindColletion(dispatch, type, colletion); },
    deleteReducer: (type, colletion, component, documentId) => { DbCrud.dbDelete(dispatch, type, colletion, component, documentId); }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CountriesList);
