import { connect } from 'react-redux';
import React, { Component } from 'react';
import { CSVLink } from "react-csv";
import { Row, Form, FormGroup, NavLink,Input,Collapse, CardHeader, Col, CardBody, Card } from 'reactstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import CurrencyFormat from 'react-currency-format';
import DbCrud from '../../../Actions/DbCrud';


class Edit extends Component {

  constructor(props) {
    super(props);
    this.state = {
      orders: {
        pag:"1",
        limit:"20",
        filFile:"order",
        filValue: ""+this.props.match.params.Order,
        sorts:[
          {
            file:"oderDate",
            value:1
          }
        ],
        rol:this.props.userdate.rol

      }
    }

  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    this.props.loadReducer('loadOrdersEdit', "orders/find/edit",this.state.orders);
  }

  callList() { 
    this.props.history.push('/Orders/List'); 
  }

  callSave(e) {
    e.preventDefault();
    //this.props.update('updateGeo', "geo", "CountriesList", this, this.state.documentId);
    this.props.history.push('/Orders/List');
  }

  selects(select, valSeleted){
    const formOrders = [];
    var Nodes = "";
    if(select == "units") Nodes = this.props.obj.listOrdersEditUnits; 
    if(select == "status") Nodes = this.props.obj.listOrdersEditStatus; 
    Object.keys(Nodes).forEach(function(key){
      var keys = select+""+key;
      var text = (select == "units")?Nodes[key]["name"]+" - "+Nodes[key]["supplier"]:Nodes[key]["name"];
      //console.log(Nodes[key]);
      if(valSeleted == Nodes[key]["name"])
         formOrders.push(<option key={keys} value={Nodes[key]["name"]} disabled>{text}</option>);
      else
        formOrders.push(<option key={keys} value={Nodes[key]["name"]}>{text}</option>);
    })
    return formOrders;
  }

  programer(valSeleted, idStatus){
    var values = document.getElementById(idStatus);
    this.props.loadReducer('loadOrdersUpdate', "orders/edit/update",{_id:values.name,file:valSeleted,value:values.value});
  }

  htmlOrdHead(nodo){
    const date = Date.parse(nodo.date);
    const formattedDate = Intl.DateTimeFormat('es-CO',{ year: 'numeric', month: 'short',day: '2-digit' }).format(date);
    return (
      <Card>
        <CardHeader id="headingOne">
          <h5 className="m-0 p-0"><strong>Compra:</strong> {formattedDate} <strong>Orden:</strong> {nodo.order} </h5>
        </CardHeader>
        <CardBody key="headOrder">
        <Row>
          <Col xs="3" md="3">
            <Row>
              <Col xs="12" md="12"><strong>Orden:</strong> {nodo.order}</Col>
              <Col xs="12" md="12"><strong>Cliente:</strong> {nodo.customer}</Col>
              <Col xs="12" md="12"><strong>DNI:</strong> {nodo.dni}</Col>
            </Row>
          </Col>
          <Col xs="3" md="3">
            <Row>
              <Col xs="12" md="12"><strong>Fecha:</strong> {(nodo.date).substr(0,10)}</Col>
              <Col xs="12" md="12"><strong>Celular:</strong> {nodo.phone}</Col>
              <Col xs="12" md="12"><strong>Entregar:</strong> {nodo.deliveryOnName}</Col>
            </Row>
          </Col>
          <Col xs="6" md="6">
            <Row>
              <Col xs="12" md="12"><strong>Departamento:</strong> {nodo.state}</Col>
              <Col xs="12" md="12"><strong>Ciudad:</strong> {nodo.city}</Col>
              <Col xs="12" md="12"><strong>Dirección:</strong> {nodo.address} {nodo.addAddress} - {nodo.neighborhood}</Col>
            </Row>
          </Col>
        </Row>
      </CardBody>
      </Card>
    );
  }

  formOrders(){

    const htmlOrdHead = this.htmlOrdHead.bind(this);
    const orders = this.props.obj.listOrdersEdit;
    const history = this.props.obj.listOrdersEditHistory;
    const formOrders = [];
    const reference = this.props.match.params.Reference;
    const selects = this.selects.bind(this);
    const programer = this.programer.bind(this);
    var flag = true;

    Object.keys(orders).forEach(function(key){
      var idStatus = "status_"+orders[key]._id;
      var idUnit = "unit_"+orders[key]._id;
      var keyname = "order"+key;
      var color = (reference == orders[key].reference)? "#a9a9a347":"";
      //Realizamos el encabezado 

      if(flag){
        flag = false;
        formOrders.push(
          <Card>
            <CardBody style={{
            borderBottom:"1px solid rgb(193, 193, 193)",
            borderTop:"1px solid rgb(193, 193, 193)", 
            padding:"10px 10px 10px 10px",
            }}>
              <Row>
                <Col>{orders[key].customer}</Col>
                <Col><strong>DNI:</strong> {orders[key].dni}</Col>
              </Row>
            </CardBody>
          </Card>
        );
        formOrders.push(htmlOrdHead(orders[key]));
      }

      //Adicionamos cada uno de los nodos 
      formOrders.push(
        <div key={keyname}>
          <CardBody style={{
            borderBottom:"1px solid rgb(193, 193, 193)",
            borderTop:"1px solid rgb(193, 193, 193)", 
            padding:"10px 10px 10px 10px",
            backgroundColor:color
            }}>
            <Row>
              <Col xs="2" md="2">
                <Row>
                  <Col xs="12" md="12">
                    <Input type="select" onChange={programer.bind(this,"status",idStatus)} name={orders[key]._id} id={idStatus} key={orders[key].reference}>
                      {selects("status",orders[key].status)}
                    </Input>
                  </Col>
                  <Col>&nbsp;</Col>
                  <Col xs="12" md="12">
                    <Input type="select" onChange={programer.bind(this,"unit",idUnit)} name={orders[key]._id} id={idUnit} key={orders[key].reference}>
                      {selects("units",orders[key].units)}
                    </Input>
                  </Col>
                </Row>
              </Col>
              <Col xs="1" md="1" style={{textAlign:"center"}}>
                <img src="https://solutionssap.com/wp-content/uploads/2016/05/Dummy.png" alt="Img Prod" style={{maxWidth:"60px", maxHeight:"60px"}} />
              </Col>
              <Col xs="9" md="9">
                <Row>
                  <Col xs="4" md="4">
                    <Row><strong>Estado:</strong>{orders[key].status}</Row>
                    <Row><strong>{orders[key].skuName}</strong></Row>
                    <Row><strong>Ref:</strong>{orders[key].reference}&nbsp;-&nbsp;<strong>Cod:</strong>{orders[key].code}</Row>
                  </Col>
                  <Col xs="2" md="2">
                    <Row><strong>Cantidad:</strong>({orders[key].lot})</Row>
                    <Row>
                      <strong>Valor:</strong>&nbsp; 
                      <CurrencyFormat value={(orders[key].value != null)?orders[key].value:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Descuento:</strong>&nbsp;
                      <CurrencyFormat value={(orders[key].discount != null)?orders[key].discount:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                  </Col>
                  <Col xs="2" md="2">
                    <Row>
                      <strong>Puntos:</strong>&nbsp; 
                      <CurrencyFormat value={(orders[key].points != null)?orders[key].points:0} displayType={'text'} thousandSeparator={true} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Precio:</strong>&nbsp; 
                      <CurrencyFormat value={(orders[key].price != null)?orders[key].price:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Envio:</strong>&nbsp;
                      <CurrencyFormat value={(orders[key].shipping != null)?orders[key].shipping:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                  </Col>
                  <Col xs="2" md="2">
                    <Row>
                      <strong>Total:</strong>&nbsp; 
                      <CurrencyFormat value={(orders[key].totalPay != null)?orders[key].totalPay:0} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                    </Row>
                    <Row>
                      <strong>Trans:</strong>&nbsp;<a href={orders[key].trakingUrl} target="_blank">{orders[key].carrier}</a>
                    </Row>
                    <Row>
                      <strong>Guia:</strong>&nbsp;{orders[key].guide}
                    </Row>
                  </Col >
                  <Col xs="2" md="2">
                    <Row><strong>Factura:</strong>&nbsp;<a href={orders[key].invoiceUrl} target="_blank">{orders[key].invoice}</a></Row>
                    <Row><strong>Marca:</strong>&nbsp;{orders[key].brand}</Row>
                    <Row><strong>Tienda:</strong>&nbsp;{orders[key].store}</Row>
                  </Col>
                </Row>
              </Col>
            </Row>
          </CardBody>
        </div>
      )
    })

    Object.keys(history).forEach(function(key){
      var keyname = "history"+key;
      var flagH = true;
      //console.log(history[key]);

      if(flagH){
        flagH = false;
        formOrders.push(htmlOrdHead(history[key][0]));
      }

    })

    return formOrders;

  }

  render() {

    return (
      <div className="animated fadeIn">
        <Card>
          <CardHeader>
            Programación de Ordenes
            <div className="card-header-actions">
              <CSVLink data={this.props.obj.listOrdersEdit}>Descargar Busqueda</CSVLink>
            </div>
          </CardHeader>
          {this.formOrders()}
        </Card>
      </div>
    );
  }

}

const mapStateToProps = (state) => {
  //Registra el objeto la funcion que cargara el objeto en el estado
  return{
    obj:state.DaoOrders,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body);
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Edit);
