import React, { Component } from 'react';
import { Pagination, PaginationItem, PaginationLink } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDoubleLeft, faAngleLeft, faAngleRight, faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons';


class ObjPag extends Component {

    constructor(props) {
        super(props);
    }

    Action(val){
        console.log(this.props);
        console.log(val);
    }

    pagRender(e){

        const RegPerFind = this.props.info.RegPerFind;
        const NumPages = this.props.info.NumPages;
        const LimtPage = this.props.info.LimtPage;
        const NowPage = this.props.info.NowPage;
        var rendPage = 5;

        rendPage = (NumPages < 10)? NumPages : 10;

        const pag = []; 

        for (let i=1; i<=rendPage; i++) {
          pag.push(
            <PaginationItem>
                <PaginationLink onClick={this.Action.bind(this, "1")}>
                    {i}
                </PaginationLink>
            </PaginationItem>
          )
        }

        return pag;
    }

    render(){
        return (

            <Pagination>
                <PaginationItem >
                    <PaginationLink first onClick={this.Action.bind(this, 'atras')}>
                        <FontAwesomeIcon  icon={faAngleDoubleLeft} />
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem >
                    <PaginationLink previous onClick={this.Action.bind(this, "1atras")}>
                        <FontAwesomeIcon  icon={faAngleLeft} />
                    </PaginationLink>
                </PaginationItem>

                {this.pagRender()}

                <PaginationItem >
                    <PaginationLink first onClick={this.Action.bind(this, "adelante")}>
                        <FontAwesomeIcon  icon={faAngleRight} />
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem >
                    <PaginationLink previous onClick={this.Action.bind(this, "adelante1")}>
                        <FontAwesomeIcon  icon={faAngleDoubleRight} />
                    </PaginationLink>
                </PaginationItem>
            </Pagination>
        );
    }
}

export default ObjPag;
