import React, { Component } from 'react';
import { connect } from 'react-redux';
import { CSVLink } from "react-csv";
import { Modal, ModalHeader, ModalBody, ModalFooter, Row, InputGroup, InputGroupAddon, InputGroupText, Form, FormGroup, Input, Col, Card, CardHeader, CardBody, Button, ButtonGroup } from 'reactstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import { AppSwitch } from '@coreui/react'
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import DbCrud from '../../../Actions/DbCrud';
import Pagination from '../Pagination/Pagination';
import CurrencyFormat from 'react-currency-format';
import { CommonLoading } from 'react-loadingg';
import { Image, Transformation } from 'cloudinary-react';
import { CloudinaryContext } from "cloudinary-react";

var flat = true;

/**
 * Compoenente que permite listar todos los paises que existen en la base de datos
*/
class List extends Component {

  constructor(props) {
    super(props);
    this.state = {
      loader:false,
      modal:false,
      IdDelete:null,
      nameDelete:null,
      filters: {
        idSupplier:this.props.userdate.idSupplier,
        rol:this.props.userdate.rol,
        pag:"1",
        limit:"10",
        filFile:"",
        filValue:"",
        sorts:[
          {
            file:"cDate",
            value:1
          }
        ]
      }
    }

    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 5,
      hidePageListOnlyOnePage: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false
    }

    this.pagLoad = this.pagLoad.bind(this);
    //this.toggleDanger = this.toggleDanger.bind(this);
  }

  /**Metodo que se ejecuta automaticamente cada vez que el componente es llamado */
  componentDidMount() {
    if (flat) {
      flat = false;
      this.setState({loader:!this.state.loader});
      this.props.loadReducer('loadProducts', "products/find",this.state.filters, this.closeLoader.bind(this));
    }
  }

  closeLoader(data){
    this.setState({loader:false, modal:false});
  }

  //Funcion que permite hacer la llamada al formulario para editar un nuevo registro 
  callEdit(row) {
    console.log(row.sku);
    console.log("edit");
    if(row.sku == undefined){
      var date = new Date();
      var idSupplier = this.props.userdate.idSupplier;
      var idTmp = date.getSeconds()+""+(date.getMinutes()+date.getHours()+date.getMonth()+date.getFullYear())+""+idSupplier.substring(20,24);
      row['sku'] = "BOB"+idTmp;
      console.log(row);
    }
    localStorage.setItem('row', JSON.stringify(row));
    localStorage.removeItem("activeTab");
    localStorage.removeItem("discount");
    localStorage.removeItem("objDiscounts");
    localStorage.removeItem("images");
    localStorage.setItem('listCategories',JSON.stringify(this.props.obj.listCategories));
    localStorage.setItem('listBrands',JSON.stringify(this.props.obj.listBrands));
    localStorage.setItem('listSuppliers',JSON.stringify(this.props.obj.listSuppliers));
    localStorage.setItem('listTaxes',JSON.stringify(this.props.obj.listTaxes));
    localStorage.setItem('listStores',JSON.stringify(this.props.obj.listStores));
    var router = '/Products/Edit/'
    if(row._id != "NaN") router = '/Products/Edit/'+row.code+'/'+row.reference+"/"+row._id
    this.props.history.push({
      pathname: router,
      row:row
    })
  }

  createProduct(){
    var date = new Date();
    var idSupplier = this.props.userdate.idSupplier;
    var idTmp = date.getSeconds()+date.getMinutes()+date.getHours()+date.getMonth()+date.getFullYear()+""+idSupplier.substring(20,24);

    var row = {
      update:true,
      sku:"BOB"+idTmp,
      cDate:date+"",
      status:true,
      idBrand:"5f31926268ac81b2a0082879",
