          else obj.push(<option key={keys} value={tmpData[key][i]}>{tmpData[key][i]}</option>)
        }
      }
    })
    return obj;
    */
  }

  imgProduct(cell, row){
    try{
      if(row.images.length > 0){
        return (
          <CloudinaryContext cloudName={this.props.userdate.cloudName} uploadPreset={this.props.userdate.uploadPreset}>
            <Image publicId={row.images[0].url} >
              <Transformation width="80" crop="scale" />
            </Image>
          </CloudinaryContext>
        )
    }
    }catch(e){}
  }

  render() {
    return (
      <div className="animated">
        <Card>
          <CardHeader>
            Lista Productos
            <div className="card-header-actions">
              <Row>
                <Button size="sm" style={{fontSize:"medium"}} onClick={this.createProduct.bind(this)} className="float-right btn btn-success active">
                  + Crear Producto
                </Button>
                {/*<Col xs="12" md="12"><CSVLink data={this.props.obj.listProducts}>Descargar Busqueda</CSVLink></Col>*/}
              </Row>
            </div>
          </CardHeader>
          <CardBody>
            <Row>
              <Col xs="12" md="12">
                <div className="pull-right">{this.renderPagination()}</div>
              </Col>
            </Row>
          </CardBody>
          <CardBody>
              <BootstrapTable  data={this.props.obj.listProducts} striped hover options={this.options} search multiColumnSearch strictSearch > 
              <TableHeaderColumn width={'8%'} dataFormat={this.imgProduct.bind(this)} >Imagen</TableHeaderColumn>
              <TableHeaderColumn width={'8%'} dataField='reference' >Referencia</TableHeaderColumn>
              <TableHeaderColumn width={'10%'} dataField='code' >Codigo</TableHeaderColumn>
              <TableHeaderColumn width={'30%'} dataField='name' >Nombre</TableHeaderColumn>
              <TableHeaderColumn width={'10%'} dataFormat={this.nameBrand.bind(this)} >Marca</TableHeaderColumn>
              <TableHeaderColumn width={'10%'} dataFormat={this.nameSuppliers.bind(this)} >Proveedor</TableHeaderColumn>
              <TableHeaderColumn width={'4%'} dataFormat={this.nameTaxes.bind(this)} >Tax</TableHeaderColumn>
              {/*<TableHeaderColumn width={'8%'} dataFormat={this.editCost.bind(this)} dataAlign='right'>Costo</TableHeaderColumn>*/}
              <TableHeaderColumn width={'8%'} dataFormat={this.editPvp.bind(this)} dataAlign='right'>Tiendas</TableHeaderColumn>
              <TableHeaderColumn width={'8%'} dataFormat={this.editPvpC.bind(this)} dataAlign='center'>Catalogo</TableHeaderColumn>
              <TableHeaderColumn dataFormat={this.editFormatter.bind(this)} width={'8%'} dataAlign='center' >Accion</TableHeaderColumn>
              <TableHeaderColumn isKey={true} dataField='_id' hidden>Identifier ID</TableHeaderColumn>
            </BootstrapTable>
          </CardBody>

          <CardBody>
            <Row>
              <Col xs="12" md="12">
                <div className="pull-right">{this.renderPagination()}</div>
              </Col>
            </Row>
          </CardBody>

        </Card>
        <Modal isOpen={this.state.modal} toggle={this.toogleClose.bind(this)} className={'modal-danger '}>
          <ModalHeader toggle={this.toogleClose.bind(this)}>Alerta</ModalHeader>
          <ModalBody>Esta seguro que desea eliminar el producto {this.state.nameDelete}</ModalBody>
          <ModalFooter>
            <Button color="danger" onClick={this.deleteProduct.bind(this)} >Eliminar</Button>{' '}
            <Button color="secondary" onClick={this.toogleClose.bind(this)}>Cancel</Button>
          </ModalFooter>
        </Modal>
        <Modal isOpen={this.state.loader} className={'modal-lg'}>
          <div style={{position:"inherit",top:"300px"}}><CommonLoading size="large"/></div>
        </Modal>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  //Registra el objeto la funcion que cargara el objeto en el estado
  return{
    obj:state.DaoProducts
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadReducer: (type, colletion, body, callFunction,) => {
      DbCrud.dbMLoad(dispatch, type, colletion, body, "POST" ,callFunction);
    },
    delete: (type, colletion, id, callFunction,) => {
      DbCrud.dbMLoad(dispatch, type, colletion, {_id:id}, "PUT" ,callFunction);
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(List);
