import { connect } from 'react-redux';
import React, { Component } from 'react';
import DbCrud from '../../../Pojo/DbCrud';
import {
  Badge, Button, ButtonDropdown, Card, CardBody, CardFooter,
  CardHeader, Col, Collapse, DropdownItem, DropdownMenu,
  DropdownToggle, Fade, Form, FormGroup, FormText, FormFeedback,
  Input, InputGroup, InputGroupAddon, InputGroupText, Label, Row,
} from 'reactstrap';

import { TextMask, InputAdapter } from 'react-text-mask-hoc';

var flat = true;

class MyAccount extends Component {

  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.toggleFade = this.toggleFade.bind(this);
    this.state = {
      collapse: true,
      fadeIn: true,
      timeout: 300
    };
  }

  componentDidMount(){this.props.dispReducer('loadCompany',"company", "email",this.props.email);}

  componentDidUpdate(){
    if(flat){
      flat=false;
      this.props.dispReducer('loadCompany', "company", "email",this.props.email);
    }
  }


  toggle() {
    this.setState({ collapse: !this.state.collapse });
  }

  toggleFade() {
    this.setState((prevState) => { return { fadeIn: !prevState }});
  }

  callCountryList(){this.props.history.push('/Geo/CountriesList');}

  callSave(){
    console.log('verificando los valores');
  }

  render() {
    return (
      <div className="animated fadeIn">

        <Row>
          <Col xs="12" sm="12">
            <Card>
              <CardHeader>
                <strong>Crear nuevo Pa√≠s</strong>
                <Button color="secondary" onClick={this.callCountryList.bind(this)} className="float-right"><i className="fa icon-arrow-left-circle"></i>&nbsp;Regresar</Button>
              </CardHeader>
              <CardBody>

              <Form className="form-horizontal">
                  <FormGroup row className="my-0">
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="countryName">Nombre Pais</Label>
                        <Input type="text" id="countryName" placeholder="Numero de la cuenta" defaultValue={this.props.obj.banks[0].title} />
                      </FormGroup>
                    </Col>
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="isoCode">Codigo ISO</Label>
                        <Input type="number" id="isoCode" placeholder="Nombre del titular" defaultValue={this.props.obj.banks[0].number} />
                      </FormGroup>
                    </Col>
                  </FormGroup>

                  <FormGroup row className="my-0">
                    <Col xs="6">
                      <FormGroup>
                        <Label htmlFor="type">Estado</Label>
                        <Input type="select" name="type" id="active" required>
                          <option value="1">Activo</option>
                          <option value="0">Inactivo</option>
                        </Input> 
                      </FormGroup>
                    </Col>
                    <Col xs="3">
                      <FormGroup>
                        <Label htmlFor="isoCode">Geo Latitud</Label>
                        <Input type="text" id="Longitud" placeholder="Longitud" defaultValue={this.props.obj.logo}/>
                      </FormGroup>
                    </Col>
                    <Col xs="3">
                      <FormGroup>
                        <Label htmlFor="isoCode">Geo Longitud</Label>
                        <Input type="text" id="Longitud" placeholder="Longitud" defaultValue={this.props.obj.logo}/>
                      </FormGroup>
                    </Col>
                  </FormGroup>
                      <div className="form-actions">
                        <Button color="primary" onClick={this.callSave.bind(this)}>Save changes</Button>&nbsp;&nbsp;
                        <Button color="secondary" onClick={this.callCountryList.bind(this)}>Cancel</Button>
                      </div>
                </Form>

              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
    return{
      email: state.login.email,
      password: state.login.password,
      obj: state.MyAccount
    }
}

const mapDispatchToProps = (dispatch) => {
    return{ 
      dispReducer: (type, colletion, file, value) => { 
        DbCrud.dbCreate(dispatch, type, colletion);
      } 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
